import React, { useState } from 'react';
import { QRCodeSVG } from 'qrcode.react';

const CertificateTemplate = ({ certificateData = {} }) => {
  const [copied, setCopied] = useState(false);

  const {
    name = "Jacqueline Thompson",
    event = "Advanced Web Development Workshop",
    description = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proesent rutrum maximus mauris sed sodales. Ut rhoncus lectus in nisi eu tempus. Proin justo eros, mollis laoreet massa non, tincidunt pharetra leo.",
    type = "Participation",
    organizer = "Tech Education Institute",
    date = new Date().toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    }),
    certificateId = `CERT-${Date.now().toString(36).toUpperCase()}-${Math.floor(Math.random() * 1000).toString().padStart(3, '0')}`
  } = certificateData;

  const handleCopyId = () => {
    navigator.clipboard.writeText(certificateId);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handlePrint = () => {
    window.print();
  };

  const qrData = JSON.stringify({
    certificateId: certificateId,
    name: name,
    event: event,
    verifyUrl: `${window.location.origin}/verify/${certificateId}`
  });

  return (
    <div style={styles.pageContainer}>
      {/* Certificate Display Section */}
      <div style={styles.certificateSection}>
        <div id="certificate-print" style={styles.certificateContainer}>
          {/* Certificate Border */}
          <div style={styles.topBorder}></div>
          <div style={styles.bottomBorder}></div>
          
          {/* Certificate Content */}
          <div style={styles.certificateContent}>
            
            {/* Header */}
            <div style={styles.header}>
              <div style={styles.headerTitle}>
                <h1 style={styles.certificateTitle}>CERTIFICATE</h1>
                <h2 style={styles.certificateSubtitle}>OF PARTICIPATION</h2>
              </div>
              <div style={styles.certificateIdBadge}>
                <span style={styles.idLabel}>ID:</span>
                <span style={styles.idValue}>{certificateId}</span>
              </div>
            </div>

            {/* Main Content */}
            <div style={styles.mainContent}>
              <p style={styles.presentationText}>
                This certificate is proudly presented to
              </p>
              
              <div style={styles.nameSection}>
                <h2 style={styles.recipientName}>{name}</h2>
                <div style={styles.nameUnderline}></div>
              </div>

              <p style={styles.descriptionText}>
                {description}
              </p>

              {/* Signatures */}
              <div style={styles.signaturesSection}>
                <div style={styles.signatureColumn}>
                  <div style={styles.signatureLine}></div>
                  <p style={styles.signatureName}>Korina Villanueva</p>
                  <p style={styles.signatureTitle}>Event Coordinator</p>
                </div>
                <div style={styles.signatureColumn}>
                  <div style={styles.signatureLine}></div>
                  <p style={styles.signatureName}>Mariana Napolitani</p>
                  <p style={styles.signatureTitle}>Director of Education</p>
                </div>
              </div>

              {/* Footer Details */}
              <div style={styles.footerDetails}>
                <div style={styles.footerItem}>
                  <span style={styles.footerLabel}>Date:</span>
                  <span style={styles.footerValue}>{date}</span>
                </div>
                <div style={styles.footerItem}>
                  <span style={styles.footerLabel}>Event:</span>
                  <span style={styles.footerValue}>{event}</span>
                </div>
                <div style={styles.footerItem}>
                  <span style={styles.footerLabel}>Certificate ID:</span>
                  <span style={styles.footerValue}>{certificateId}</span>
                </div>
              </div>
            </div>

            {/* QR Code */}
            <div style={styles.qrSection}>
              <div style={styles.qrContainer}>
                <QRCodeSVG
                  value={qrData}
                  size={80}
                  level="H"
                  bgColor="#ffffff"
                  fgColor="#000000"
                />
                <p style={styles.qrText}>Scan to verify</p>
              </div>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div style={styles.actionButtons}>
          <button onClick={handlePrint} style={styles.printButton}>
            üñ®Ô∏è Print Certificate
          </button>
          <button onClick={handleCopyId} style={styles.copyButton}>
            {copied ? '‚úì Copied!' : 'üìã Copy ID'}
          </button>
        </div>
      </div>

      {/* Certificate Details Sidebar */}
      <div style={styles.detailsSidebar}>
        <div style={styles.detailsCard}>
          <h3 style={styles.detailsTitle}>Certificate Details</h3>
          
          <div style={styles.detailItem}>
            <span style={styles.detailLabel}>Recipient Name</span>
            <span style={styles.detailValue}>{name}</span>
          </div>
          
          <div style={styles.detailItem}>
            <span style={styles.detailLabel}>Certificate Type</span>
            <span style={styles.detailBadge}>{type} Certificate</span>
          </div>
          
          <div style={styles.detailItem}>
            <span style={styles.detailLabel}>Issuing Organization</span>
            <span style={styles.detailValue}>{organizer}</span>
          </div>
          
          <div style={styles.detailItem}>
            <span style={styles.detailLabel}>Date Issued</span>
            <span style={styles.detailValue}>{date}</span>
          </div>
          
          <div style={styles.detailItem}>
            <span style={styles.detailLabel}>Certificate ID</span>
            <div style={styles.idContainer}>
              <span style={styles.idDisplay}>{certificateId}</span>
              <button 
                onClick={handleCopyId} 
                style={styles.smallCopyButton}
              >
                {copied ? '‚úì' : 'üìã'}
              </button>
            </div>
          </div>
          
          <div style={styles.verificationSection}>
            <h4 style={styles.verificationTitle}>Verification</h4>
            <p style={styles.verificationText}>
              This certificate can be verified using the QR code or Certificate ID.
            </p>
            <div style={styles.verificationQr}>
              <QRCodeSVG
                value={qrData}
                size={120}
                level="H"
                bgColor="#ffffff"
                fgColor="#000000"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  pageContainer: {
