import React, { useState } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { generatePDF } from '../services/pdfGenerator';

const CertificateTemplate = ({ certificateData = {} }) => {
  const [copied, setCopied] = useState(false);
  const [downloading, setDownloading] = useState(false);

  // Default data
  const data = {
    name: certificateData.name || "abhi",
    event: certificateData.event || "Cloud Bootcamp",
    description: certificateData.description || "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proesent rutrum maximus mauris sed sodales. Ut rhoncus lectus in nisi eu tempus. Proin justo eros, mollis laoreet massa non, tincidunt pharetra leo.",
    type: certificateData.type || "Winner Certificate",
    organizer: certificateData.organizer || "Computer Science Department",
    date: certificateData.date || "December 18, 2025",
    certificateId: certificateData.certificateId || `CERT-${Date.now()}-${Math.floor(Math.random() * 1000)}`
  };

  // Generate QR data
  const qrData = JSON.stringify({
    certificateId: data.certificateId,
    name: data.name,
    event: data.event,
    date: data.date,
    verifyUrl: `${window.location.origin}/verify/${data.certificateId}`
  });

  const handleCopyId = () => {
    navigator.clipboard.writeText(data.certificateId);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handleDownloadPDF = async () => {
    setDownloading(true);
    try {
      const result = await generatePDF(data);
      if (result && !result.success) {
        alert(result.error || 'Failed to generate PDF');
      }
    } catch (error) {
      console.error('PDF download error:', error);
      alert('Error downloading PDF. Please try again.');
    } finally {
      setDownloading(false);
    }
  };

  const handlePrint = () => {
    const printContent = document.getElementById('certificate-print').innerHTML;
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Certificate - ${data.name}</title>
          <style>
            body { 
              margin: 0; 
              padding: 0; 
              background: white;
              font-family: 'Times New Roman', serif;
              display: flex;
              justify-content: center;
              align-items: center;
              min-height: 100vh;
            }
            @media print {
              body { margin: 0; }
              @page { margin: 0; }
            }
          </style>
        </head>
        <body>
          <div style="
            width: 210mm;
            min-height: 297mm;
            padding: 60px 80px;
            box-sizing: border-box;
            background: white;
            position: relative;
          ">
            ${printContent}
          </div>
          <script>
            window.onload = () => {
              window.print();
              setTimeout(() => window.close(), 1000);
            }
          </script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  return (
    <div style={styles.pageContainer}>
      {/* Certificate Preview */}
      <div style={styles.certificatePreview}>
        <div id="certificate-print" style={styles.certificate}>
          {/* Top Border */}
          <div style={styles.topBorder}></div>
          
          {/* Header */}
          <div style={styles.header}>
            <h1 style={styles.mainTitle}>CERTIFICATE</h1>
            <h2 style={styles.subTitle}>OF PARTICIPATION</h2>
            <p style={styles.presentationText}>
              This certificate is proudly presented to
            </p>
          </div>

          {/* Recipient Name */}
          <div style={styles.recipientContainer}>
            <h2 style={styles.recipientName}>{data.name}</h2>
          </div>

          {/* Description */}
          <div style={styles.descriptionContainer}>
            <p style={styles.description}>
              {data.description}
            </p>
          </div>

          {/* Signatures and Details */}
          <div style={styles.bottomSection}>
            <div style={styles.signatureSection}>
              <div style={styles.signatureBlock}>
                <div style={styles.signatureLine}></div>
                <p style={styles.signatureName}>Korina Villanueva</p>
                <p style={styles.signatureTitle}>Event Coordinator</p>
              </div>
              
              <div style={styles.dateBlock}>
                <p style={styles.dateLabel}>DATE:</p>
                <p style={styles.dateValue}>{data.date.split(' ')[2]}, 2025</p>
              </div>

              <div style={styles.signatureBlock}>
                <div style={styles.signatureLine}></div>
                <p style={styles.signatureName}>Mariana Napolitani</p>
                <p style={styles.signatureTitle}>Director of Education</p>
              </div>
            </div>

            <div style={styles.eventInfo}>
              <p style={styles.eventLabel}>EVENT:</p>
              <p style={styles.eventValue}>{data.event}</p>
            </div>

            <div style={styles.certIdInfo}>
              <p style={styles.certIdLabel}>CERTIFICATION ID:</p>
              <p style={styles.certIdValue}>{data.certificateId}</p>
            </div>
          </div>

          {/* QR Code */}
          <div style={styles.qrSection}>
            <div style={styles.qrContainer}>
              <QRCodeSVG
                value={qrData}
                size={80}
                level="H"
                bgColor="#ffffff"
                fgColor="#000000"
              />
              <p style={styles.qrText}>Scan to verify</p>
            </div>
          </div>

          {/* Bottom Border */}
          <div style={styles.bottomBorder}></div>
        </div>

        {/* Action Buttons */}
        <div style={styles.actionButtons}>
          <button 
            onClick={handleDownloadPDF} 
            style={styles.downloadBtn}
            disabled={downloading}
          >
            {downloading ? '‚è≥ Generating PDF...' : '‚¨áÔ∏è Download PDF'}
          </button>
