import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

export const generatePDF = async (certificateData) => {
  return new Promise((resolve) => {
    // Create temporary div with certificate
    const tempDiv = document.createElement('div');
    tempDiv.style.position = 'absolute';
    tempDiv.style.left = '-9999px';
    tempDiv.innerHTML = `
      <div id="pdf-certificate" style="width: 800px; padding: 20px;">
        <div style="
          width: 100%;
          background: linear-gradient(135deg, #1a2980 0%, #26d0ce 100%);
          color: white;
          padding: 30px;
          text-align: center;
          border-radius: 10px 10px 0 0;
        ">
          <h1 style="margin: 0 0 10px 0; font-size: 32px;">CERTIFICATE OF ACHIEVEMENT</h1>
          <p style="margin: 0; opacity: 0.9;">Official Digital Certificate</p>
        </div>
        
        <div style="
          padding: 40px;
          background: #fffef7;
          border: 2px solid #d4af37;
          border-radius: 0 0 10px 10px;
          text-align: center;
        ">
          <p style="font-size: 18px; color: #555;">This certifies that</p>
          
          <h2 style="
            font-size: 48px;
            color: #1a2980;
            margin: 20px 0;
            font-family: 'Brush Script MT', cursive;
          ">${certificateData.name}</h2>
          
          <p style="font-size: 18px; color: #555;">has successfully completed</p>
          
          <h3 style="
            font-size: 32px;
            color: #e74c3c;
            font-style: italic;
            margin: 20px 0;
          ">"${certificateData.event}"</h3>
          
          <p style="font-size: 18px; color: #555;">with distinction as</p>
          
          <div style="
            display: inline-block;
            background: #3498db;
            color: white;
            padding: 12px 30px;
            font-size: 20px;
            font-weight: bold;
            border-radius: 30px;
            margin: 20px 0;
          ">${certificateData.type.toUpperCase()}</div>
          
          <div style="
            background: #f8f9fa;
            padding: 20px;
            border-radius: 8px;
            margin: 30px 0;
            text-align: left;
            display: inline-block;
          ">
            <p><strong>Certificate ID:</strong> ${certificateData.certificateId}</p>
            <p><strong>Date:</strong> ${certificateData.date}</p>
            <p><strong>Issued By:</strong> ${certificateData.organizer}</p>
          </div>
          
          <div style="margin-top: 40px; text-align: center;">
            <p style="color: #666; font-size: 14px;">
              âœ… Digitally Signed | ðŸ”’ Secure Certificate | ðŸ“± QR Verifiable
            </p>
          </div>
        </div>
      </div>
    `;
    
    document.body.appendChild(tempDiv);
    
    setTimeout(() => {
      html2canvas(tempDiv.querySelector('#pdf-certificate')).then(canvas => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF('l', 'mm', 'a4');
        const imgWidth = 297; // A4 width in mm
        const imgHeight = canvas.height * imgWidth / canvas.width;
        
        pdf.addImage(imgData, 'PNG', 0, 0, imgWidth, imgHeight);
        pdf.save(`${certificateData.name}_${certificateData.certificateId}.pdf`);
        
        document.body.removeChild(tempDiv);
        resolve();
      });
    }, 500);
  });
};

export const generateAllPDFs = async (certificates) => {
  for (const cert of certificates) {
    await generatePDF(cert);
  }
};
