import React from 'react';
import { QRCodeSVG } from 'qrcode.react';

const CertificateTemplate = ({ certificateData = {} }) => {
  // Default values if no data provided
  const data = {
    name: certificateData.name || 'John Doe',
    event: certificateData.event || 'Sample Event',
    type: certificateData.type || 'Participation',
    organizer: certificateData.organizer || 'College Name',
    date: certificateData.date || new Date().toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    }),
    certificateId: certificateData.certificateId || `CERT-${Date.now()}-1001`
  };

  const qrData = JSON.stringify({
    id: data.certificateId,
    name: data.name,
    event: data.event,
    type: data.type,
    date: data.date
  });

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <h1 style={styles.title}>CERTIFICATE OF ACHIEVEMENT</h1>
        <p style={styles.subtitle}>Official Digital Certificate</p>
      </div>

      {/* Body */}
      <div style={styles.body}>
        <p style={styles.text}>This is to certify that</p>
        
        <h2 style={styles.name}>{data.name}</h2>
        
        <p style={styles.text}>has successfully completed</p>
        
        <h3 style={styles.event}>"{data.event}"</h3>
        
        <p style={styles.text}>with distinction as</p>
        
        <div style={styles.typeBadge}>
          {data.type.toUpperCase()}
        </div>
        
        <p style={styles.text}>organized by {data.organizer}</p>
        
        <div style={styles.details}>
          <div style={styles.detailItem}>
            <span>Certificate ID:</span>
            <strong style={styles.id}>{data.certificateId}</strong>
          </div>
          <div style={styles.detailItem}>
            <span>Date:</span>
            <strong>{data.date}</strong>
          </div>
        </div>

        {/* QR Code */}
        <div style={styles.qrContainer}>
          <QRCodeSVG
            value={qrData}
            size={100}
            level="H"
            bgColor="#ffffff"
            fgColor="#000000"
          />
          <p style={styles.qrText}>Scan to verify</p>
        </div>
      </div>

      {/* Footer */}
      <div style={styles.footer}>
        <span>âœ… Digitally Signed</span>
        <span>ðŸ”’ Secure Certificate</span>
        <span>ðŸ“± QR Verifiable</span>
      </div>
    </div>
  );
};

const styles = {
  container: {
    width: '800px',
    minHeight: '600px',
    backgroundColor: 'white',
    border: '10px solid #f0f0f0',
    borderRadius: '10px',
    boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
    margin: '20px auto',
    fontFamily: 'Arial, sans-serif',
    overflow: 'hidden'
  },
  header: {
    backgroundColor: '#1a2980',
    color: 'white',
    padding: '30px',
    textAlign: 'center'
  },
  title: {
    margin: '0 0 10px 0',
    fontSize: '32px',
    fontWeight: 'bold'
  },
  subtitle: {
    margin: '0',
    fontSize: '16px',
    opacity: '0.9'
  },
  body: {
    padding: '40px',
    textAlign: 'center',
    background: 'linear-gradient(to bottom, #ffffff, #f9f9f9)'
  },
  text: {
    fontSize: '18px',
    color: '#333',
    margin: '15px 0'
  },
  name: {
    fontSize: '42px',
    color: '#1a2980',
    margin: '20px 0',
    fontWeight: 'bold'
  },
  event: {
    fontSize: '28px',
    color: '#e74c3c',
    margin: '20px 0',
    fontStyle: 'italic'
  },
  typeBadge: {
    display: 'inline-block',
    backgroundColor: '#3498db',
    color: 'white',
    padding: '10px 30px',
    fontSize: '20px',
    fontWeight: 'bold',
    borderRadius: '50px',
    margin: '20px 0'
  },
  details: {
    backgroundColor: '#f8f9fa',
    padding: '20px',
    borderRadius: '10px',
    margin: '30px 0',
    textAlign: 'left',
    display: 'inline-block'
  },
  detailItem: {
    margin: '10px 0',
    display: 'flex',
    justifyContent: 'space-between',
    minWidth: '300px'
  },
  id: {
    color: '#e74c3c',
    fontFamily: 'monospace'
  },
  qrContainer: {
    backgroundColor: 'white',
    padding: '15px',
    borderRadius: '10px',
    border: '2px solid #ddd',
    display: 'inline-block',
    marginTop: '20px'
  },
  qrText: {
    margin: '10px 0 0 0',
    fontSize: '14px',
    color: '#666'
  },
  footer: {
    backgroundColor: '#2c3e50',
    color: 'white',
    padding: '15px',
    display: 'flex',
    justifyContent: 'space-around',
    alignItems: 'center'
  }
};

export default CertificateTemplate;
