        </div>

        <!-- CERT ID -->
        <div style="
          position:absolute;
          bottom:30px;
          right:40px;
          font-size:11px;
          font-family:monospace;
          text-align:center;
        ">
          Certificate ID<br/>
          <strong>${certificateData.certificateId}</strong>
        </div>

        <!-- BOTTOM BORDER -->
        <div style="
          position:absolute;
          bottom:0; left:0; right:0;
          height:12px;
          background:#2c3e50;
        "></div>

      </div>
    `;

    document.body.appendChild(tempDiv);

    // ===============================
    // HIGH-QUALITY CANVAS
    // ===============================
    const canvas = await html2canvas(
      tempDiv.querySelector("#pdf-certificate"),
      {
        scale: 2, // ðŸ”¥ HIGH QUALITY
        useCORS: true,
        backgroundColor: "#ffffff"
      }
    );

    const imgData = canvas.toDataURL("image/png");

    // ===============================
    // PDF (A4 LANDSCAPE)
    // ===============================
    const pdf = new jsPDF("l", "mm", "a4");
    pdf.addImage(imgData, "PNG", 0, 0, 297, 210);
    pdf.save(
      `${certificateData.name}_${certificateData.certificateId}.pdf`
    );

    document.body.removeChild(tempDiv);
    resolve();
  });
};

// ===============================
// BULK GENERATION
// ===============================
export const generateAllPDFs = async (certificates) => {
  for (const cert of certificates) {
    await generatePDF(cert);
  }
};
