import React, { useState } from 'react';
import { verifyCertificate } from '../services/api';
import CertificateTemplate from '../components/CertificateTemplate';

const VerifyCertificate = () => {
  const [certificateId, setCertificateId] = useState('');
  const [verificationResult, setVerificationResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showScanner, setShowScanner] = useState(false);

  const handleVerify = async () => {
    if (!certificateId.trim()) {
      setVerificationResult({
        valid: false,
        error: 'Please enter a Certificate ID'
      });
      return;
    }

    setLoading(true);
    setVerificationResult(null);

    try {
      // In a real app, you would call your backend API
      // const result = await verifyCertificate(certificateId);
      
      // For demo purposes, simulate API response
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Mock verification logic
      const isValid = certificateId.startsWith('CERT-');
      let mockResult;
      
      if (isValid) {
        mockResult = {
          valid: true,
          data: {
            name: getRandomName(),
            event: getRandomEvent(),
            type: getRandomType(),
            certificateId: certificateId,
            date: new Date().toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric'
            }),
            organizer: "College Department",
            generatedOn: new Date().toISOString()
          }
        };
      } else {
        mockResult = {
          valid: false,
          error: 'Invalid Certificate ID format. IDs start with "CERT-"'
        };
      }
      
      setVerificationResult(mockResult);
    } catch (error) {
      console.error('Verification error:', error);
      setVerificationResult({
        valid: false,
        error: 'Error connecting to verification service'
      });
    } finally {
      setLoading(false);
    }
  };

  // Helper functions for mock data
  const getRandomName = () => {
    const names = ['Rahul Kumar', 'Abhiram KH', 'Anjali S', 'Priya Sharma', 'John Doe'];
    return names[Math.floor(Math.random() * names.length)];
  };

  const getRandomEvent = () => {
    const events = ['Cloud Bootcamp', 'AMS Workshop', 'AI Seminar', 'Web Development', 'Robotics Workshop'];
    return events[Math.floor(Math.random() * events.length)];
  };

  const getRandomType = () => {
    const types = ['Winner', 'First Prize', 'Second Prize', 'Participation', 'Excellence Award'];
    return types[Math.floor(Math.random() * types.length)];
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleVerify();
    }
  };

  const handleSampleVerify = () => {
    setCertificateId('CERT-1766052911430-1001');
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <h1 style={styles.title}>ğŸ” Verify Certificate</h1>
        <p style={styles.subtitle}>Check the authenticity of any certificate issued by our system</p>
      </div>

      {/* Verification Input Section */}
      <div style={styles.inputSection}>
        <div style={styles.inputCard}>
          <h3 style={styles.inputTitle}>Enter Certificate ID</h3>
          
          <div style={styles.inputGroup}>
            <div style={styles.inputContainer}>
              <input
                type="text"
                placeholder="e.g., CERT-1766052911430-1001"
                value={certificateId}
                onChange={(e) => setCertificateId(e.target.value.toUpperCase())}
                onKeyPress={handleKeyPress}
                style={styles.input}
              />
              <div style={styles.inputHint}>
                <span style={styles.hintIcon}>ğŸ’¡</span>
                Certificate IDs start with "CERT-" followed by numbers
              </div>
            </div>
            
            <div style={styles.buttonGroup}>
              <button
                onClick={handleVerify}
                disabled={loading || !certificateId.trim()}
                style={loading ? styles.buttonLoading : styles.buttonVerify}
              >
                {loading ? (
                  <>
                    <span style={styles.spinner}></span>
                    Verifying...
                  </>
                ) : (
                  <>
                    <span style={styles.buttonIcon}>âœ…</span>
                    Verify Now
                  </>
                )}
              </button>
              
              <button
                onClick={handleSampleVerify}
                style={styles.buttonSample}
              >
                <span style={styles.buttonIcon}>ğŸ“‹</span>
                Try Sample ID
              </button>
            </div>
          </div>

          <div style={styles.alternativeSection}>
            <div style={styles.alternativeDivider}>
              <span style={styles.dividerText}>OR</span>
            </div>
            
            <div style={styles.scannerSection}>
              <button
                onClick={() => setShowScanner(!showScanner)}
                style={styles.scannerButton}
              >
                <span style={styles.scannerIcon}>ğŸ“·</span>
                {showScanner ? 'Hide Scanner' : 'Scan QR Code'}
              </button>
              
              {showScanner && (
                <div style={styles.scannerPlaceholder}>
                  <div style={styles.scannerMockup}>
                    <div style={styles.qrScanner}>
                      <div style={styles.qrFrame}></div>
                      <div style={styles.scanLine}></div>
                    </div>
                    <p style={styles.scannerHint}>Point camera at certificate QR code</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Results Section */}
      {verificationResult && (
        <div style={styles.resultsSection}>
          {verificationResult.valid ? (
            <div style={styles.successCard}>
              <div style={styles.successHeader}>
                <div style={styles.successIcon}>âœ…</div>
                <div>
                  <h2 style={styles.successTitle}>Certificate Verified!</h2>
                  <p style={styles.successSubtitle}>This certificate is valid and authentic</p>
                </div>
              </div>
              
              <div style={styles.verificationDetails}>
                <div style={styles.detailGrid}>
                  <div style={styles.detailItem}>
                    <span style={styles.detailLabel}>Status:</span>
                    <span style={styles.detailValueValid}>âœ“ VALID</span>
                  </div>
                  <div style={styles.detailItem}>
                    <span style={styles.detailLabel}>Issued To:</span>
                    <span style={styles.detailValue}>{verificationResult.data.name}</span>
                  </div>
                  <div style={styles.detailItem}>
                    <span style={styles.detailLabel}>Event:</span>
                    <span style={styles.detailValue}>{verificationResult.data.event}</span>
                  </div>
                  <div style={styles.detailItem}>
                    <span style={styles.detailLabel}>Award:</span>
                    <span style={styles.detailValue}>{verificationResult.data.type}</span>
                  </div>
                  <div style={styles.detailItem}>
                    <span style={styles.detailLabel}>Issue Date:</span>
                    <span style={styles.detailValue}>{verificationResult.data.date}</span>
                  </div>
                  <div style={styles.detailItem}>
                    <span style={styles.detailLabel}>Verification Time:</span>
                    <span style={styles.detailValue}>{new Date().toLocaleTimeString()}</span>
                  </div>
                </div>
              </div>
              
              <div style={styles.certificatePreview}>
                <h3 style={styles.previewTitle}>Certificate Preview</h3>
                <div style={{ transform: 'scale(0.8)', transformOrigin: 'top center' }}>
                  <CertificateTemplate certificateData={verificationResult.data} />
                </div>
              </div>
              
              <div style={styles.actions}>
                <button style={styles.actionButton}>
                  <span style={styles.actionIcon}>ğŸ–¨ï¸</span>
                  Print Verification Report
                </button>
                <button style={styles.actionButtonSecondary}>
                  <span style={styles.actionIcon}>ğŸ“§</span>
                  Share Verification
                </button>
              </div>
            </div>
          ) : (
            <div style={styles.errorCard}>
              <div style={styles.errorHeader}>
                <div style={styles.errorIcon}>âŒ</div>
                <div>
                  <h2 style={styles.errorTitle}>Verification Failed</h2>
                  <p style={styles.errorSubtitle}>This certificate could not be verified</p>
                </div>
              </div>
              
              <div style={styles.errorDetails}>
                <p style={styles.errorMessage}>
                  <strong>Reason:</strong> {verificationResult.error}
                </p>
                
                <div style={styles.troubleshoot}>
                  <h4 style={styles.troubleshootTitle}>Troubleshooting Tips:</h4>
                  <ul style={styles.troubleshootList}>
                    <li>Check that the Certificate ID is entered correctly</li>
                    <li>Ensure the ID starts with "CERT-"</li>
                    <li>Try scanning the QR code instead of typing</li>
                    <li>Contact the issuing organization if the problem persists</li>
                  </ul>
                </div>
                
                <div style={styles.sampleIds}>
                  <h4 style={styles.sampleTitle}>Sample Valid IDs:</h4>
                  <div style={styles.sampleList}>
                    <code style={styles.sampleId}>CERT-1766052911430-1001</code>
                    <code style={styles.sampleId}>CERT-1766052911430-1002</code>
                    <code style={styles.sampleId}>CERT-1766052911430-1003</code>
                  </div>
                </div>
              </div>
              
              <button
                onClick={handleSampleVerify}
                style={styles.tryAgainButton}
              >
                <span style={styles.buttonIcon}>ğŸ”„</span>
                Try with Sample ID
              </button>
            </div>
          )}
        </div>
      )}

      {/* Information Section */}
      <div style={styles.infoSection}>
        <h3 style={styles.infoTitle}>How Certificate Verification Works</h3>
        
        <div style={styles.infoGrid}>
          <div style={styles.infoCard}>
            <div style={styles.infoIcon}>ğŸ”</div>
            <h4 style={styles.infoCardTitle}>Secure Validation</h4>
            <p style={styles.infoCardText}>
