import axios from 'axios';

const API_BASE_URL = "https://oqzu30zmf8.execute-api.eu-north-1.amazonaws.com/prod";// Your backend URL

export const processCSVData = async (certificates) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/certificates/bulk`, {
      certificates
    });
    return response.data;
  } catch (error) {
    console.error('Error processing CSV:', error);
    throw error;
  }
};

export const verifyCertificate = async (certificateId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/certificates/verify/${certificateId}`);
    return response.data;
  } catch (error) {
    console.error('Error verifying certificate:', error);
    throw error;
  }
};

export const generateCertificatePDF = async (certificateData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/certificates/generate-pdf`, 
      certificateData,
      { responseType: 'blob' }
    );
    return response.data;
  } catch (error) {
    console.error('Error generating PDF:', error);
    throw error;
  }
};
