import React from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { saveCertificate } from '../services/certificateStorage';

const CertificateTemplate = ({ certificateData = {} }) => {
  // Ensure certificate is saved to storage
  React.useEffect(() => {
    if (certificateData && certificateData.certificateId && !certificateData._saved) {
      saveCertificate(certificateData);
    }
  }, [certificateData]);

  const {
    name = "Student Name",
    event = "Event Name",
    type = "Participation",
    organizer = "Issuing Organization",
    date = new Date().toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    }),
    certificateId = `CERT-${Date.now()}-${Math.floor(Math.random() * 10000)}`
  } = certificateData;

  // QR Code contains the certificate ID for verification
  const qrData = JSON.stringify({
    certificateId: certificateId,
    name: name,
    event: event,
    type: type,
    date: date,
    verifyUrl: `${window.location.origin}/verify/${certificateId}`
  });

  return (
    <div id="certificate-print" style={styles.container}>
      {/* Certificate Border */}
      <div style={styles.border}></div>
      
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.seal}>
          <span style={styles.sealIcon}>üèÜ</span>
        </div>
        <h1 style={styles.title}>CERTIFICATE OF ACHIEVEMENT</h1>
        <p style={styles.subtitle}>Officially Issued & Digitally Verified</p>
      </div>

      {/* Body */}
      <div style={styles.body}>
        <p style={styles.text}>This certifies that</p>
        
        <div style={styles.nameContainer}>
          <h2 style={styles.name}>{name}</h2>
        </div>
        
        <p style={styles.text}>has successfully completed</p>
        
        <div style={styles.eventContainer}>
          <h3 style={styles.event}>"{event}"</h3>
        </div>
        
        <p style={styles.text}>with distinction as</p>
        
        <div style={styles.awardContainer}>
          <span style={styles.award}>{type.toUpperCase()}</span>
        </div>
        
        <div style={styles.details}>
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>Certificate ID:</span>
            <span style={styles.detailValue}>{certificateId}</span>
          </div>
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>Date of Issue:</span>
            <span style={styles.detailValue}>{date}</span>
          </div>
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>Issued By:</span>
            <span style={styles.detailValue}>{organizer}</span>
          </div>
        </div>

        {/* QR Code for Verification */}
        <div style={styles.qrSection}>
          <div style={styles.qrContainer}>
            <QRCodeSVG
              value={qrData}
              size={120}
              level="H"
              includeMargin={true}
              bgColor="#ffffff"
              fgColor="#2c3e50"
            />
            <p style={styles.qrLabel}>Scan to Verify Authenticity</p>
            <p style={styles.qrId}>{certificateId}</p>
          </div>
        </div>

        {/* Signature */}
        <div style={styles.signatureSection}>
          <div style={styles.signatureLine}></div>
          <p style={styles.signatureName}>Authorized Signatory</p>
          <p style={styles.signatureTitle}>Head of Institution</p>
        </div>
      </div>

      {/* Footer */}
      <div style={styles.footer}>
        <div style={styles.footerItem}>
          <span style={styles.footerIcon}>‚úÖ</span>
          <span>Digitally Signed</span>
        </div>
        <div style={styles.footerItem}>
          <span style={styles.footerIcon}>üîí</span>
          <span>Securely Stored</span>
        </div>
        <div style={styles.footerItem}>
          <span style={styles.footerIcon}>üì±</span>
          <span>QR Verifiable</span>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    width: '800px',
    minHeight: '600px',
    backgroundColor: '#fffef7',
    border: '15px solid #f5f5f5',
    borderRadius: '10px',
    boxShadow: '0 15px 50px rgba(0,0,0,0.15)',
    position: 'relative',
    fontFamily: "'Georgia', 'Times New Roman', serif",
    margin: '20px auto',
    overflow: 'hidden'
  },
  border: {
    position: 'absolute',
    top: '5px',
    left: '5px',
    right: '5px',
    bottom: '5px',
    border: '2px solid #d4af37',
    borderRadius: '5px',
    pointerEvents: 'none'
  },
  header: {
    background: 'linear-gradient(135deg, #1a2980 0%, #26d0ce 100%)',
    padding: '30px 20px',
    textAlign: 'center',
    color: 'white',
    position: 'relative'
  },
  seal: {
    position: 'absolute',
    top: '-25px',
    left: '50%',
    transform: 'translateX(-50%)',
    width: '60px',
    height: '60px',
    backgroundColor: '#ffd700',
    borderRadius: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    border: '4px solid white',
    boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
  },
  sealIcon: {
    fontSize: '28px'
  },
  title: {
    margin: '0 0 10px 0',
    fontSize: '2.5em',
    fontWeight: 'bold',
    letterSpacing: '2px'
  },
  subtitle: {
    margin: '0',
    fontSize: '1.1em',
    opacity: '0.9',
    letterSpacing: '1px'
  },
  body: {
    padding: '40px 50px',
    textAlign: 'center',
    background: 'url("https://www.transparenttextures.com/patterns/cream-paper.png")'
  },
  text: {
    fontSize: '1.2em',
    color: '#555',
    margin: '10px 0'
  },
  nameContainer: {
    margin: '20px 0'
  },
  name: {
    fontSize: '3em',
    color: '#1a2980',
    margin: '0',
    fontFamily: "'Brush Script MT', cursive",
    fontWeight: 'bold'
  },
  eventContainer: {
    margin: '20px 0'
  },
  event: {
    fontSize: '2em',
    color: '#e74c3c',
    margin: '0',
    fontStyle: 'italic',
    display: 'inline-block',
    padding: '10px 20px',
    borderLeft: '3px solid #3498db',
    borderRight: '3px solid #3498db'
  },
  awardContainer: {
    margin: '20px 0'
  },
  award: {
    display: 'inline-block',
    backgroundColor: '#3498db',
    color: 'white',
    padding: '10px 30px',
    fontSize: '1.5em',
    fontWeight: 'bold',
    borderRadius: '30px',
    boxShadow: '0 4px 15px rgba(52, 152, 219, 0.3)'
  },
  details: {
    backgroundColor: '#f8f9fa',
    padding: '20px',
    borderRadius: '10px',
