import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { saveCertificate } from '../services/certificateStorage';
import CertificateTemplate from '../components/CertificateTemplate';

const GenerateCertificate = () => {
  const navigate = useNavigate();
  const [certificateData, setCertificateData] = useState({
    name: '',
    event: '',
    description: '',
    organizer: '',
    date: new Date().toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    }),
    type: 'Participation'
  });
  const [preview, setPreview] = useState(false);
  const [generatedId, setGeneratedId] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCertificateData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const certificateId = saveCertificate(certificateData);
    setGeneratedId(certificateId);
    setPreview(true);
    
    // Scroll to preview
    setTimeout(() => {
      const previewElement = document.getElementById('certificate-preview');
      if (previewElement) {
        previewElement.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  };

  const handleGenerateAnother = () => {
    setCertificateData({
      name: '',
      event: '',
      description: '',
      organizer: '',
      date: new Date().toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      }),
      type: 'Participation'
    });
    setPreview(false);
    setGeneratedId(null);
  };

  const handleViewAll = () => {
    navigate('/certificates');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Generate Certificate</h1>
        <p style={styles.subtitle}>
          Create a digital certificate for a single recipient
        </p>
      </div>

      <div style={styles.content}>
        {/* Form Section */}
        {!preview ? (
          <form onSubmit={handleSubmit} style={styles.form}>
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Recipient Name *
                </label>
                <input
                  type="text"
                  name="name"
                  value={certificateData.name}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder="Enter recipient's full name"
                  required
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Event Name *
                </label>
                <input
                  type="text"
                  name="event"
                  value={certificateData.event}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder="e.g., Cloud Bootcamp 2024"
                  required
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Description
                </label>
                <textarea
                  name="description"
                  value={certificateData.description}
                  onChange={handleChange}
                  style={styles.textarea}
                  placeholder="Description of achievement..."
                  rows="4"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Issuing Organization *
                </label>
                <input
                  type="text"
                  name="organizer"
                  value={certificateData.organizer}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder="e.g., Computer Science Department"
                  required
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Certificate Type
                </label>
                <select
                  name="type"
                  value={certificateData.type}
                  onChange={handleChange}
                  style={styles.select}
                >
                  <option value="Participation">Participation</option>
                  <option value="Achievement">Achievement</option>
                  <option value="Excellence">Excellence</option>
                  <option value="Completion">Completion</option>
                  <option value="Winner">Winner</option>
                </select>
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Date of Issue
                </label>
                <input
                  type="date"
                  name="date"
                  value={certificateData.date}
                  onChange={(e) => {
                    const date = new Date(e.target.value);
                    setCertificateData(prev => ({
                      ...prev,
                      date: date.toLocaleDateString('en-US', {
                        year: 'numeric',
                        month: 'long',
                        day: 'numeric'
                      })
                    }));
                  }}
                  style={styles.input}
                />
              </div>
            </div>

            <div style={styles.formActions}>
              <button type="submit" style={styles.submitButton}>
                ‚ú® Generate Certificate
              </button>
              <button 
                type="button" 
                style={styles.previewButton}
                onClick={() => setPreview(true)}
              >
                üëÅÔ∏è Preview
              </button>
            </div>
          </form>
        ) : (
          <div style={styles.successCard}>
