              </button>
              <button onClick={handleViewAll} style={styles.viewAllButton}>
                üìã View All Certificates
              </button>
            </div>
          </div>
        )}

        {/* Certificate Preview */}
        {preview && certificateData.name && (
          <div id="certificate-preview" style={styles.previewSection}>
            <h2 style={styles.previewTitle}>Certificate Preview</h2>
            <div style={styles.previewContainer}>
              <CertificateTemplate certificateData={certificateData} />
            </div>
            <div style={styles.previewActions}>
              <p style={styles.previewNote}>
                üí° Tip: Use the buttons in the preview to download PDF or print the certificate
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

