import React, { useState, useRef } from 'react';
import Papa from 'papaparse';
import { saveMultipleCertificates } from '../services/certificateStorage';
import { generatePDF } from '../services/pdfGenerator'; // Updated import

const UploadCSV = () => {
  const [csvData, setCsvData] = useState([]);
  const [parsedData, setParsedData] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [generating, setGenerating] = useState(false);
  const [progress, setProgress] = useState({ current: 0, total: 0 });
  const [results, setResults] = useState([]);
  const fileInputRef = useRef(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    setResults([]);

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        setCsvData(results.data);
        const formattedData = results.data.map((row, index) => ({
          name: row.name || row.Name || row.NAME || `Recipient ${index + 1}`,
          event: row.event || row.Event || row.EVENT || 'Training Program',
          description: row.description || row.Description || row.DESCRIPTION || 
            'For successful completion and outstanding performance',
          organizer: row.organizer || row.Organizer || row.ORGANIZER || 'Certificate Authority',
          date: row.date || row.Date || row.DATE || new Date().toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          }),
          type: row.type || row.Type || row.TYPE || 'Participation'
        }));
        setParsedData(formattedData);
        setUploading(false);
      },
      error: (error) => {
        console.error('CSV parsing error:', error);
        alert('Error parsing CSV file. Please check the format.');
        setUploading(false);
      }
    });
  };

  const handleGenerateCertificates = async () => {
    if (parsedData.length === 0) return;

    setGenerating(true);
    setProgress({ current: 0, total: parsedData.length });
    const results = [];

    try {
      // Save certificates to storage
      const certificateIds = saveMultipleCertificates(parsedData);
      
      // Generate PDFs one by one
      for (let i = 0; i < parsedData.length; i++) {
        const cert = parsedData[i];
        setProgress({ current: i + 1, total: parsedData.length });
        
        try {
          console.log(`Generating PDF ${i + 1}/${parsedData.length} for ${cert.name}`);
          const result = await generatePDF(cert);
          
          results.push({
            certificate: cert,
            success: true,
            fileName: result?.fileName || `${cert.name.replace(/\s+/g, '_')}.pdf`
          });
          
          // Add delay between generations to avoid overwhelming
          if (i < parsedData.length - 1) {
            await new Promise(resolve => setTimeout(resolve, 1000));
          }
        } catch (error) {
          console.error(`Failed to generate PDF for ${cert.name}:`, error);
          results.push({
            certificate: cert,
            success: false,
            error: error.message || 'Failed to generate PDF'
          });
        }
      }
      
      setResults(results);
      alert(`Processed ${parsedData.length} certificates. ${results.filter(r => r.success).length} successful, ${results.filter(r => !r.success).length} failed.`);
    } catch (error) {
      console.error('Error generating certificates:', error);
      alert('Error generating certificates. Please try again.');
    } finally {
      setGenerating(false);
    }
  };

  const downloadTemplate = () => {
    const template = [
      ['name', 'event', 'description', 'organizer', 'date', 'type'],
      ['John Doe', 'Web Development Bootcamp', 'For outstanding performance in web development course', 'Tech Institute', 'December 18, 2024', 'Excellence'],
      ['Jane Smith', 'Data Science Workshop', 'For successful completion of data science training', 'Data Academy', 'December 18, 2024', 'Participation'],
      ['Bob Johnson', 'Cloud Certification', 'For achieving cloud certification with distinction', 'Cloud University', 'December 18, 2024', 'Completion']
    ];

    const csvContent = template.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'certificate_template.csv';
    a.click();
  };

  const resetUpload = () => {
    setCsvData([]);
    setParsedData([]);
    setResults([]);
    setProgress({ current: 0, total: 0 });
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Bulk Certificate Upload</h1>
        <p style={styles.subtitle}>
          Upload a CSV file to generate multiple certificates at once
        </p>
      </div>

      <div style={styles.content}>
        {/* Upload Section */}
        <div style={styles.uploadCard}>
          <div style={styles.uploadHeader}>
            <span style={styles.uploadIcon}>üì§</span>
            <h2 style={styles.uploadTitle}>Upload CSV File</h2>
          </div>
          
          <div style={styles.uploadArea}>
            <input
              type="file"
              accept=".csv"
              onChange={handleFileUpload}
              ref={fileInputRef}
              style={styles.fileInput}
              id="csv-upload"
            />
            <label htmlFor="csv-upload" style={styles.uploadLabel}>
              <div style={styles.uploadBox}>
                <span style={styles.uploadBoxIcon}>üìÑ</span>
                <p style={styles.uploadBoxText}>
                  {uploading ? 'Processing CSV...' : 'Click to upload or drag and drop'}
                </p>
                <p style={styles.uploadBoxSubtext}>
                  CSV file with certificate data
                </p>
              </div>
            </label>
          </div>

          <div style={styles.templateSection}>
            <p style={styles.templateText}>Need a template?</p>
            <button onClick={downloadTemplate} style={styles.templateButton}>
              üì• Download CSV Template
            </button>
          </div>
        </div>

        {/* Preview Section */}
        {parsedData.length > 0 && (
          <div style={styles.previewCard}>
            <div style={styles.previewHeader}>
              <h2 style={styles.previewTitle}>
                Preview ({parsedData.length} certificates)
              </h2>
              <button onClick={resetUpload} style={styles.resetButton}>
                üóëÔ∏è Clear
              </button>
            </div>

            <div style={styles.tableContainer}>
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th style={styles.th}>#</th>
                    <th style={styles.th}>Name</th>
                    <th style={styles.th}>Event</th>
                    <th style={styles.th}>Organizer</th>
                    <th style={styles.th}>Type</th>
                  </tr>
                </thead>
                <tbody>
                  {parsedData.slice(0, 5).map((row, index) => (
                    <tr key={index} style={styles.tr}>
                      <td style={styles.td}>{index + 1}</td>
                      <td style={styles.td}>{row.name}</td>
                      <td style={styles.td}>{row.event}</td>
                      <td style={styles.td}>{row.organizer}</td>
                      <td style={styles.td}>
                        <span style={styles.badge}>{row.type}</span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              
              {parsedData.length > 5 && (
                <p style={styles.moreText}>
                  ... and {parsedData.length - 5} more certificates
                </p>
              )}
            </div>

            <div style={styles.actionSection}>
              <button
                onClick={handleGenerateCertificates}
                disabled={generating}
                style={styles.generateButton}
              >
                {generating ? (
                  <>
                    <span style={styles.spinner}></span>
                    Generating {progress.current}/{progress.total}...
                  </>
                ) : (
                  '‚ú® Generate All Certificates'
                )}
              </button>
              
              {generating && (
                <div style={styles.progressBar}>
                  <div 
                    style={{
                      ...styles.progressFill,
                      width: `${(progress.current / progress.total) * 100}%`
                    }}
                  ></div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Results Section */}
        {results.length > 0 && (
          <div style={styles.resultsCard}>
            <h2 style={styles.resultsTitle}>Generation Results</h2>
            <div style={styles.resultsGrid}>
              <div style={styles.resultStat}>
                <span style={styles.resultStatNumber}>
                  {results.filter(r => r.success).length}
                </span>
                <span style={styles.resultStatLabel}>Successful</span>
              </div>
              <div style={styles.resultStat}>
                <span style={styles.resultStatNumber}>
                  {results.filter(r => !r.success).length}
                </span>
                <span style={styles.resultStatLabel}>Failed</span>
              </div>
            </div>
            
            <div style={styles.resultsList}>
              {results.slice(0, 3).map((result, index) => (
                <div key={index} style={styles.resultItem}>
                  <span style={styles.resultIcon}>
                    {result.success ? '‚úÖ' : '‚ùå'}
                  </span>
                  <span style={styles.resultName}>
                    {result.certificate.name}
                  </span>
                  <span style={styles.resultStatus}>
                    {result.success ? 'Generated' : 'Failed'}
                  </span>
                </div>
              ))}
              
              {results.length > 3 && (
                <p style={styles.moreResults}>
                  ... and {results.length - 3} more results
                </p>
              )}
            </div>
            
            <div style={styles.resultsActions}>
              <button onClick={resetUpload} style={styles.newUploadButton}>
                üì§ Upload Another CSV
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
