import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';
import { verifyCertificate, getCertificate } from '../services/certificateStorage';

const VerifyCertificate = () => {
  const { id } = useParams();
  const [certificateId, setCertificateId] = useState(id || '');
  const [verificationResult, setVerificationResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [certificateData, setCertificateData] = useState(null);

  useEffect(() => {
    if (id) {
      handleVerify(id);
    }
  }, [id]);

  const handleVerify = async (idToVerify = null) => {
    const verifyId = idToVerify || certificateId;
    if (!verifyId.trim()) {
      alert('Please enter a Certificate ID');
      return;
    }

    setLoading(true);
    setVerificationResult(null);
    setCertificateData(null);

    try {
      const result = verifyCertificate(verifyId);
      setVerificationResult(result);
      
      if (result.valid) {
        setCertificateData(result.data);
      }
    } catch (error) {
      console.error('Verification error:', error);
      setVerificationResult({
        valid: false,
        error: 'An error occurred during verification',
        suggestions: ['Please try again later']
      });
    } finally {
      setLoading(false);
    }
  };

  const handleScanQR = () => {
    // This would integrate with a QR scanner in a real app
    alert('QR scanner would open here in a real application');
  };

  const handleClear = () => {
    setCertificateId('');
    setVerificationResult(null);
    setCertificateData(null);
  };

  const copyCertificateId = () => {
    if (certificateData?.certificateId) {
      navigator.clipboard.writeText(certificateData.certificateId);
      alert('Certificate ID copied to clipboard!');
    }
  };

  const generateQRData = () => {
    if (!certificateData) return '';
    return JSON.stringify({
      certificateId: certificateData.certificateId,
      name: certificateData.name,
      event: certificateData.event,
      date: certificateData.date,
      verifyUrl: `${window.location.origin}/verify/${certificateData.certificateId}`
    });
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Verify Certificate</h1>
        <p style={styles.subtitle}>
          Verify the authenticity of a certificate using its ID or QR code
        </p>
      </div>

      <div style={styles.content}>
        {/* Verification Input */}
        <div style={styles.inputCard}>
          <div style={styles.inputHeader}>
            <h2 style={styles.inputTitle}>Enter Certificate ID</h2>
            <button onClick={handleScanQR} style={styles.scanButton}>
              üì± Scan QR Code
            </button>
          </div>
          
          <div style={styles.inputGroup}>
            <input
              type="text"
              value={certificateId}
              onChange={(e) => setCertificateId(e.target.value)}
              placeholder="Enter Certificate ID (e.g., CERT-123456789)"
              style={styles.input}
              disabled={loading}
            />
            <div style={styles.inputActions}>
              <button
                onClick={() => handleVerify()}
                disabled={loading || !certificateId.trim()}
                style={styles.verifyButton}
              >
                {loading ? (
                  <>
                    <span style={styles.spinner}></span>
                    Verifying...
                  </>
                ) : (
                  'üîç Verify Certificate'
                )}
              </button>
              <button onClick={handleClear} style={styles.clearButton}>
                üóëÔ∏è Clear
              </button>
            </div>
          </div>
          
          <p style={styles.inputHelp}>
            üí° You can find the Certificate ID at the bottom of the certificate or in the QR code
          </p>
        </div>

        {/* Verification Result */}
        {verificationResult && (
          <div style={styles.resultCard}>
            <div style={{
              ...styles.resultHeader,
              backgroundColor: verificationResult.valid ? '#d1fae5' : '#fee2e2'
            }}>
              <span style={styles.resultIcon}>
                {verificationResult.valid ? '‚úÖ' : '‚ùå'}
              </span>
              <h2 style={styles.resultTitle}>
                {verificationResult.valid ? 'Certificate Verified' : 'Verification Failed'}
              </h2>
            </div>
            
            <div style={styles.resultContent}>
              {verificationResult.valid ? (
                <>
                  <p style={styles.successMessage}>
                    {verificationResult.message}
                  </p>
                  
                  {certificateData && (
                    <div style={styles.certificateInfo}>
                      <div style={styles.infoGrid}>
                        <div style={styles.infoItem}>
                          <span style={styles.infoLabel}>Recipient Name</span>
                          <span style={styles.infoValue}>{certificateData.name}</span>
                        </div>
                        <div style={styles.infoItem}>
                          <span style={styles.infoLabel}>Event</span>
                          <span style={styles.infoValue}>{certificateData.event}</span>
                        </div>
                        <div style={styles.infoItem}>
                          <span style={styles.infoLabel}>Issuing Organization</span>
                          <span style={styles.infoValue}>{certificateData.organizer}</span>
                        </div>
                        <div style={styles.infoItem}>
                          <span style={styles.infoLabel}>Date Issued</span>
                          <span style={styles.infoValue}>{certificateData.date}</span>
                        </div>
                        <div style={styles.infoItem}>
                          <span style={styles.infoLabel}>Certificate Type</span>
                          <span style={styles.infoBadge}>{certificateData.type}</span>
                        </div>
                        <div style={styles.infoItem}>
                          <span style={styles.infoLabel}>Certificate ID</span>
                          <div style={styles.idContainer}>
                            <code style={styles.idValue}>{certificateData.certificateId}</code>
                            <button onClick={copyCertificateId} style={styles.copyButton}>
                              üìã Copy
                            </button>
                          </div>
                        </div>
                      </div>
                      
                      <div style={styles.qrSection}>
                        <div style={styles.qrCard}>
                          <QRCodeSVG
                            value={generateQRData()}
                            size={150}
                            level="H"
                            bgColor="#ffffff"
                            fgColor="#000000"
                          />
                          <p style={styles.qrText}>Scan to verify this certificate</p>
                        </div>
                      </div>
                    </div>
                  )}
                </>
              ) : (
                <>
                  <p style={styles.errorMessage}>
                    {verificationResult.error}
                  </p>
                  
                  {verificationResult.suggestions && (
                    <div style={styles.suggestions}>
                      <h3 style={styles.suggestionsTitle}>Suggestions:</h3>
                      <ul style={styles.suggestionsList}>
                        {verificationResult.suggestions.map((suggestion, index) => (
                          <li key={index} style={styles.suggestionItem}>
                            {suggestion}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </>
              )}
            </div>
            
            <div style={styles.resultFooter}>
              <p style={styles.timestamp}>
                Verified at: {new Date().toLocaleString()}
              </p>
              {verificationResult.valid && (
                <button onClick={() => window.print()} style={styles.printButton}>
                  üñ®Ô∏è Print Verification
                </button>
              )}
            </div>
          </div>
        )}

        {/* Verification Tips */}
        <div style={styles.tipsCard}>
          <h2 style={styles.tipsTitle}>üí° Verification Tips</h2>
          <div style={styles.tipsGrid}>
            <div style={styles.tipItem}>
              <span style={styles.tipIcon}>üîç</span>
              <h3 style={styles.tipTitle}>Check Certificate ID</h3>
              <p style={styles.tipText}>
                Every certificate has a unique ID. Verify it matches the official records.
              </p>
            </div>
            <div style={styles.tipItem}>
              <span style={styles.tipIcon}>üì±</span>
              <h3 style={styles.tipTitle}>Scan QR Code</h3>
              <p style={styles.tipText}>
                Use your phone's camera to scan the QR code for instant verification.
              </p>
            </div>
            <div style={styles.tipItem}>
              <span style={styles.tipIcon}>üîÑ</span>
              <h3 style={styles.tipTitle}>Regular Updates</h3>
              <p style={styles.tipText}>
                Certificates are updated in real-time. Always verify with the latest data.
              </p>
            </div>
            <div style={styles.tipItem}>
              <span style={styles.tipIcon}>üìû</span>
              <h3 style={styles.tipTitle}>Contact Support</h3>
              <p style={styles.tipText}>
                If verification fails, contact the issuing organization for assistance.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

