import React, { useState } from 'react';
import { QRCodeSVG } from 'qrcode.react';

const CertificateTemplate = () => {
  const [copied, setCopied] = useState(false);
  
  // Certificate data
  const certificateData = {
    name: "abhi",
    event: "Cloud Bootcamp",
    description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proesent rutrum maximus mauris sed sodales. Ut rhoncus lectus in nisi eu tempus. Proin justo eros, mollis laoreet massa non, tincidunt pharetra leo.",
    type: "Winner Certificate",
    organizer: "Computer Science Department",
    date: "December 18, 2025",
    certificateId: "CERT-1766072565981-1000"
  };

  const handleCopyId = () => {
    navigator.clipboard.writeText(certificateData.certificateId);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Certificate</title>
          <style>
            body {
              font-family: 'Times New Roman', serif;
              margin: 0;
              padding: 0;
              display: flex;
              justify-content: center;
              align-items: center;
              min-height: 100vh;
              background: #f5f5f5;
            }
            .certificate {
              width: 210mm;
              min-height: 297mm;
              background: white;
              padding: 50px;
              box-shadow: 0 0 20px rgba(0,0,0,0.1);
              position: relative;
              border: 1px solid #ddd;
            }
            @media print {
              body {
                background: white;
              }
              .certificate {
                box-shadow: none;
                border: none;
                margin: 0;
                padding: 50px;
              }
            }
          </style>
        </head>
        <body>
          <div class="certificate">
            ${document.getElementById('certificate-print').innerHTML}
          </div>
          <script>
            window.onload = function() {
              window.print();
              window.close();
            }
          </script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  const qrData = JSON.stringify({
    certificateId: certificateData.certificateId,
    name: certificateData.name,
    event: certificateData.event,
    date: certificateData.date
  });

  return (
    <div style={styles.pageContainer}>
      {/* Certificate Preview */}
      <div style={styles.certificatePreview}>
        <div id="certificate-print" style={styles.certificate}>
          {/* Top Border */}
          <div style={styles.topBorder}></div>
          
          {/* Header */}
          <div style={styles.header}>
            <h1 style={styles.mainTitle}>CERTIFICATE</h1>
            <h2 style={styles.subTitle}>OF PARTICIPATION</h2>
            <p style={styles.presentationText}>
              This certificate is proudly presented to
            </p>
          </div>

          {/* Recipient Name */}
          <div style={styles.recipientContainer}>
            <h2 style={styles.recipientName}>{certificateData.name}</h2>
          </div>

          {/* Description */}
          <div style={styles.descriptionContainer}>
            <p style={styles.description}>
              {certificateData.description}
            </p>
          </div>

          {/* Signatures and Details */}
          <div style={styles.bottomSection}>
            <div style={styles.signatureSection}>
              <div style={styles.signatureBlock}>
                <div style={styles.signatureLine}></div>
                <p style={styles.signatureName}>Korina Villanueva</p>
                <p style={styles.signatureTitle}>Event Coordinator</p>
              </div>
              
              <div style={styles.dateBlock}>
                <p style={styles.dateLabel}>DATE:</p>
                <p style={styles.dateValue}>{certificateData.date.split(' ')[2]}, 2025</p>
              </div>

              <div style={styles.signatureBlock}>
                <div style={styles.signatureLine}></div>
                <p style={styles.signatureName}>Mariana Napolitani</p>
                <p style={styles.signatureTitle}>Director of Education</p>
              </div>
            </div>

            <div style={styles.eventInfo}>
              <p style={styles.eventLabel}>EVENT:</p>
              <p style={styles.eventValue}>{certificateData.event}</p>
            </div>

            <div style={styles.certIdInfo}>
              <p style={styles.certIdLabel}>CERTIFICATION ID:</p>
              <p style={styles.certIdValue}>{certificateData.certificateId}</p>
            </div>
          </div>

          {/* Bottom Border */}
          <div style={styles.bottomBorder}></div>
        </div>

        {/* Action Buttons */}
        <div style={styles.actionButtons}>
          <button onClick={handlePrint} style={styles.printBtn}>
            üñ®Ô∏è Print Certificate
          </button>
          <button onClick={handleCopyId} style={styles.copyBtn}>
            {copied ? '‚úì Copied!' : 'üìã Copy ID'}
          </button>
        </div>
      </div>

      {/* Certificate Details Sidebar */}
      <div style={styles.detailsSidebar}>
        <div style={styles.statusBadge}>
          <div style={styles.statusDot}></div>
          Status verified
        </div>

        <div style={styles.detailsCard}>
          <h3 style={styles.detailsTitle}>Certificate Details</h3>
          
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>RECIPIENT NAME</span>
            <span style={styles.detailValue}>{certificateData.name}</span>
          </div>
          
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>CERTIFICATE TYPE</span>
            <span style={styles.typeBadge}>{certificateData.type}</span>
          </div>
          
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>ISSUING ORGANIZATION</span>
            <span style={styles.detailValue}>{certificateData.organizer}</span>
          </div>
          
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>DATE ISSUED</span>
            <span style={styles.detailValue}>{certificateData.date}</span>
          </div>
          
          <div style={styles.detailRow}>
            <span style={styles.detailLabel}>CERTIFICATE ID</span>
            <div style={styles.idContainer}>
              <span style={styles.idValue}>{certificateData.certificateId}</span>
              <button 
                onClick={handleCopyId} 
                style={styles.copyIconBtn}
              >
                {copied ? '‚úì' : 'üìã'}
              </button>
            </div>
          </div>

          <div style={styles.verificationSection}>
            <h4 style={styles.verificationTitle}>Verification</h4>
            <p style={styles.verificationText}>
              This certificate can be verified using the QR code or Certificate ID.
            </p>
            <div style={styles.qrContainer}>
              <QRCodeSVG
                value={qrData}
                size={120}
                level="H"
                bgColor="#ffffff"
                fgColor="#000000"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  pageContainer: {
    display: 'flex',
    minHeight: '100vh',
    backgroundColor: '#f8f9fa',
    fontFamily: "'Times New Roman', Times, serif",
    padding: '20px',
