import React, { useState, useRef } from 'react';
import { saveAs } from 'file-saver';
import JSZip from 'jszip';
import CertificateTemplate from '../components/CertificateTemplate';

const UploadCSV = () => {
  const [csvData, setCsvData] = useState([]);
  const [previewData, setPreviewData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [generatedCertificates, setGeneratedCertificates] = useState([]);
  const fileInputRef = useRef(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    
    reader.onload = (e) => {
      try {
        const text = e.target.result;
        const lines = text.split('\n').filter(line => line.trim() !== '');
        
        if (lines.length < 2) {
          alert('CSV file must have at least a header and one data row');
          return;
        }

        const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
        const data = [];

        for (let i = 1; i < lines.length; i++) {
          const values = lines[i].split(',').map(v => v.trim());
          if (values.length >= 2) {
            const obj = {};
            headers.forEach((header, index) => {
              if (index < values.length) {
                obj[header] = values[index];
              }
            });
            data.push(obj);
          }
        }

        // Add certificate IDs
        const dataWithIds = data.map((item, index) => ({
          ...item,
          certificateId: `CERT-${Date.now()}-${1000 + index}`,
          date: new Date().toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          }),
          signature: "Dr. John Smith",
          signatureTitle: "Principal"
        }));

        setCsvData(dataWithIds);
        setPreviewData(dataWithIds.slice(0, 3));
      } catch (error) {
        alert('Error parsing CSV file: ' + error.message);
      }
    };

    reader.readAsText(file);
  };

  const generateAllCertificates = async () => {
    if (csvData.length === 0) return;

    setLoading(true);
    try {
      const zip = new JSZip();
      const certsFolder = zip.folder('certificates');
      
      // Generate HTML files for each certificate
      csvData.forEach((cert, index) => {
        const htmlContent = generateCertificateHTML(cert);
        const fileName = `${cert.name.replace(/\s+/g, '_')}_${cert.certificateId}.html`;
        certsFolder.file(fileName, htmlContent);
      });

      // Generate summary CSV
      const summaryCSV = generateSummaryCSV(csvData);
      zip.file('certificates_summary.csv', summaryCSV);

      // Generate instructions
      const instructions = generateInstructions();
      zip.file('README.txt', instructions);

      // Generate the ZIP
      const content = await zip.generateAsync({ type: 'blob' });
      saveAs(content, `certificates_batch_${new Date().getTime()}.zip`);
      
      setGeneratedCertificates(csvData);
      alert(`‚úÖ Successfully generated ${csvData.length} certificates!`);
    } catch (error) {
      alert('Error generating certificates: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const generateCertificateHTML = (cert) => {
    return `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>Certificate - ${cert.name}</title>
    <style>
        body {
            margin: 0;
            padding: 40px;
            background: #f5f5f5;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            font-family: Arial, sans-serif;
        }
        .print-btn {
            position: fixed;
            top: 20px;
            right: 20px;
            padding: 10px 20px;
            background: #3498db;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            z-index: 1000;
        }
        .print-btn:hover {
            background: #2980b9;
        }
        @media print {
            body {
                padding: 0;
                background: white;
            }
            .print-btn {
                display: none;
            }
        }
    </style>
</head>
<body>
    <button class="print-btn" onclick="window.print()">üñ®Ô∏è Print Certificate</button>
    <div id="certificate-container"></div>
    
    <script>
        // Certificate data
        const certificateData = ${JSON.stringify(cert, null, 2)};
        
        // Create a simple HTML version for offline viewing
        const html = \`
        <div style="
            width: 800px;
            margin: 0 auto;
            background: white;
            padding: 30px;
            border-radius: 15px;
            box-shadow: 0 10px 40px rgba(0,0,0,0.1);
            font-family: Georgia, serif;
        ">
            <h1 style="text-align: center; color: #1a2980; border-bottom: 3px solid #FFD700; padding-bottom: 20px;">
                CERTIFICATE OF ACHIEVEMENT
            </h1>
            
            <p style="text-align: center; font-size: 1.2em; color: #555; margin: 30px 0;">
                This certifies that
            </p>
            
            <h2 style="text-align: center; font-size: 3em; color: #1a2980; margin: 20px 0; font-family: cursive;">
                \${certificateData.name}
            </h2>
            
            <p style="text-align: center; font-size: 1.2em; color: #666; margin: 20px 0;">
