import React, { useState } from "react";
import { QRCodeSVG } from "qrcode.react";

const CertificateTemplate = ({ certificateData = {} }) => {
  const [copied, setCopied] = useState(false);

  const {
    name = "Jacqueline Thompson",
    event = "Advanced Web Development Workshop",
    description = "This certificate is awarded in recognition of successful participation and completion of the program.",
    type = "Participation",
    organizer = "Tech Education Institute",
    date = new Date().toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    }),
    certificateId = `CERT-${Date.now().toString(36).toUpperCase()}-${Math.floor(
      Math.random() * 1000
    )
      .toString()
      .padStart(3, "0")}`,
  } = certificateData;

  const qrData = JSON.stringify({
    certificateId,
    name,
    event,
    verifyUrl: `${window.location.origin}/verify/${certificateId}`,
  });

  const handleCopy = () => {
    navigator.clipboard.writeText(certificateId);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <>
      {/* PRINT STYLES */}
      <style>{`
        @media print {
          body {
            margin: 0;
          }
          .no-print {
            display: none !important;
          }
          .certificate {
            box-shadow: none !important;
          }
        }
      `}</style>

      <div style={styles.page}>
        {/* CERTIFICATE */}
        <div style={styles.certificate} className="certificate">
          <div style={styles.borderTop} />
          <div style={styles.borderBottom} />

          {/* HEADER */}
          <div style={styles.header}>
            <div style={styles.titleBox}>
              <h1 style={styles.title}>CERTIFICATE</h1>
              <h2 style={styles.subtitle}>OF {type.toUpperCase()}</h2>
            </div>
            <div style={styles.certId}>ID: {certificateId}</div>
          </div>

          {/* CONTENT */}
          <div style={styles.content}>
            <p style={styles.intro}>This certificate is proudly presented to</p>

            <h2 style={styles.name}>{name}</h2>
            <div style={styles.underline} />

            <p style={styles.description}>{description}</p>

            {/* SIGNATURES */}
            <div style={styles.signatures}>
              <div>
                <div style={styles.signLine} />
                <p style={styles.signName}>Korina Villanueva</p>
                <p style={styles.signRole}>Event Coordinator</p>
              </div>
              <div>
                <div style={styles.signLine} />
                <p style={styles.signName}>Mariana Napolitani</p>
                <p style={styles.signRole}>Director of Education</p>
              </div>
            </div>

            {/* FOOTER */}
            <div style={styles.footer}>
              <div>
                <span style={styles.footerLabel}>Date</span>
                <strong>{date}</strong>
              </div>
              <div>
                <span style={styles.footerLabel}>Event</span>
                <strong>{event}</strong>
              </div>
              <div>
                <span style={styles.footerLabel}>Issued By</span>
                <strong>{organizer}</strong>
              </div>
            </div>
          </div>

          {/* QR */}
          <div style={styles.qr}>
            <QRCodeSVG value={qrData} size={90} level="H" />
            <span style={styles.qrText}>Scan to verify</span>
          </div>
        </div>

        {/* ACTIONS */}
        <div style={styles.actions} className="no-print">
          <button style={styles.btn} onClick={() => window.print()}>
            üñ®Ô∏è Print
          </button>
          <button style={styles.btn} onClick={handleCopy}>
            {copied ? "‚úì Copied" : "üìã Copy ID"}
          </button>
        </div>
      </div>
    </>
  );
};

const styles = {
  page: {
    background: "#f2f2f2",
    padding: "20px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    fontFamily: "'Times New Roman', serif",
  },

  /* A4 Landscape */
  certificate: {
    width: "1123px",
    height: "794px",
    background: "#fff",
    position: "relative",
    boxShadow: "0 0 25px rgba(0,0,0,0.15)",
  },

  borderTop: {
    height: "10px",
    background: "#2c3e50",
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
  },
  borderBottom: {
    height: "10px",
    background: "#2c3e50",
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
  },

  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "50px 60px 20px",
    borderBottom: "2px solid #eee",
  },

  titleBox: {
    flex: 1,
    textAlign: "center",
  },
  title: {
    fontSize: "46px",
    letterSpacing: "4px",
    margin: 0,
  },
  subtitle: {
    fontSize: "26px",
    color: "#666",
    marginTop: "8px",
  },

  certId: {
    fontSize: "12px",
    fontFamily: "monospace",
    border: "1px solid #ddd",
    padding: "6px 10px",
  },

  content: {
    padding: "40px 80px",
    textAlign: "center",
  },

  intro: {
    fontStyle: "italic",
    color: "#666",
  },

  name: {
    fontSize: "42px",
    margin: "20px 0 5px",
  },

  underline: {
    width: "300px",
    height: "2px",
    background: "#000",
    margin: "auto",
  },

  description: {
    margin: "35px auto",
    maxWidth: "800px",
    lineHeight: 1.8,
    color: "#444",
  },

  signatures: {
    display: "flex",
    justifyContent: "space-around",
    marginTop: "60px",
  },

  signLine: {
    width: "160px",
    height: "1px",
    background: "#000",
    margin: "0 auto 10px",
  },

  signName: {
    fontWeight: "bold",
  },

  signRole: {
    fontStyle: "italic",
    fontSize: "14px",
    color: "#666",
  },

  footer: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "50px",
    fontSize: "14px",
  },

  footerLabel: {
    display: "block",
    fontSize: "11px",
    color: "#777",
    textTransform: "uppercase",
  },

  qr: {
    position: "absolute",
    bottom: "30px",
    right: "40px",
    textAlign: "center",
    fontSize: "10px",
  },

  qrText: {
    marginTop: "5px",
    color: "#666",
  },

  actions: {
    marginTop: "20px",
    display: "flex",
    gap: "15px",
  },

  btn: {
    padding: "10px 20px",
    border: "none",
    cursor: "pointer",
    background: "#2c3e50",
    color: "#fff",
    borderRadius: "4px",
    fontSize: "15px",
  },
};

export default CertificateTemplate;
