import React, { useState } from "react";
import { QRCodeSVG } from "qrcode.react";

const CertificateTemplate = ({ certificateData = {} }) => {
  const [copied, setCopied] = useState(false);

  const {
    name = "Jacqueline Thompson",
    event = "Advanced Web Development Workshop",
    description =
      "This certificate is awarded in recognition of successful participation and completion of the program conducted with academic excellence.",
    type = "Participation",
    organizer = "Tech Education Institute",
    date = new Date().toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    }),
    certificateId = `CERT-${Date.now().toString(36).toUpperCase()}-${Math.floor(
      Math.random() * 1000
    )
      .toString()
      .padStart(3, "0")}`,
  } = certificateData;

  const qrData = JSON.stringify({
    certificateId,
    name,
    event,
    verifyUrl: `${window.location.origin}/verify/${certificateId}`,
  });

  const copyId = () => {
    navigator.clipboard.writeText(certificateId);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <>
      {/* PRINT STYLES */}
      <style>{`
        @media print {
          body { margin: 0; }
          .no-print { display: none !important; }
          .certificate { box-shadow: none !important; }
        }
      `}</style>

      <div style={styles.page}>
        <div style={styles.certificate} className="certificate">
          {/* Borders */}
          <div style={styles.borderTop} />
          <div style={styles.borderBottom} />

          {/* Header */}
          <div style={styles.header}>
            <h1 style={styles.title}>CERTIFICATE</h1>
            <h2 style={styles.subtitle}>OF {type.toUpperCase()}</h2>
          </div>

          {/* Body */}
          <div style={styles.content}>
            <p style={styles.intro}>This certificate is proudly presented to</p>

            <h2 style={styles.name}>{name}</h2>
            <div style={styles.underline} />

            <p style={styles.description}>{description}</p>

            {/* Signatures */}
            <div style={styles.signatures}>
              <div>
                <div style={styles.signLine} />
                <p style={styles.signName}>Korina Villanueva</p>
                <p style={styles.signRole}>Event Coordinator</p>
              </div>
              <div>
                <div style={styles.signLine} />
                <p style={styles.signName}>Mariana Napolitani</p>
                <p style={styles.signRole}>Director of Education</p>
              </div>
            </div>

            {/* Footer */}
            <div style={styles.footer}>
              <div>
                <span style={styles.footerLabel}>Date</span>
                <strong>{date}</strong>
              </div>
              <div>
                <span style={styles.footerLabel}>Event</span>
                <strong>{event}</strong>
              </div>
              <div>
                <span style={styles.footerLabel}>Issued By</span>
                <strong>{organizer}</strong>
              </div>
            </div>
          </div>

          {/* QR + CERT ID (BOTTOM RIGHT) */}
          <div style={styles.qrSection}>
            <QRCodeSVG value={qrData} size={90} level="H" />
            <div style={styles.certIdBox}>
              <span style={styles.certIdLabel}>Certificate ID</span>
              <span style={styles.certIdValue}>{certificateId}</span>
            </div>
          </div>
        </div>

        {/* Actions */}
        <div style={styles.actions} className="no-print">
          <button style={styles.btn} onClick={() => window.print()}>
            üñ®Ô∏è Print
          </button>
          <button style={styles.btn} onClick={copyId}>
            {copied ? "‚úì Copied" : "üìã Copy ID"}
          </button>
        </div>
      </div>
    </>
  );
};

const styles = {
  page: {
    background: "#f2f2f2",
    padding: "20px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    fontFamily: "'Times New Roman', serif",
  },

  /* A4 Landscape */
  certificate: {
    width: "1123px",
    height: "794px",
    background: "#fff",
    position: "relative",
    boxShadow: "0 0 25px rgba(0,0,0,0.15)",
  },

  borderTop: {
    height: "12px",
    background: "#2c3e50",
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
  },
  borderBottom: {
    height: "12px",
    background: "#2c3e50",
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
  },

  header: {
    textAlign: "center",
    paddingTop: "70px",
  },

  title: {
    fontSize: "48px",
    letterSpacing: "5px",
    margin: 0,
  },

  subtitle: {
    fontSize: "26px",
    color: "#555",
    marginTop: "10px",
  },

  content: {
    padding: "40px 90px",
    textAlign: "center",
  },

  intro: {
    fontStyle: "italic",
    color: "#666",
  },

  name: {
    fontSize: "44px",
    margin: "25px 0 6px",
  },

  underline: {
    width: "320px",
    height: "2px",
    background: "#000",
    margin: "auto",
  },

  description: {
    margin: "40px auto",
    maxWidth: "820px",
    lineHeight: 1.9,
    fontSize: "16px",
    color: "#444",
  },

  signatures: {
    display: "flex",
    justifyContent: "space-around",
    marginTop: "70px",
  },

  signLine: {
    width: "170px",
    height: "1px",
    background: "#000",
    margin: "0 auto 12px",
  },

  signName: {
    fontWeight: "bold",
  },

  signRole: {
    fontStyle: "italic",
    fontSize: "14px",
    color: "#666",
  },

  footer: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "55px",
    fontSize: "14px",
  },

  footerLabel: {
    display: "block",
    fontSize: "11px",
    color: "#777",
    textTransform: "uppercase",
  },

  qrSection: {
    position: "absolute",
    bottom: "30px",
    right: "40px",
    textAlign: "center",
  },

  certIdBox: {
    marginTop: "8px",
    fontSize: "11px",
    fontFamily: "monospace",
  },

  certIdLabel: {
    display: "block",
    color: "#777",
    textTransform: "uppercase",
    fontSize: "10px",
  },

  certIdValue: {
    fontWeight: "bold",
  },

  actions: {
    marginTop: "20px",
    display: "flex",
    gap: "15px",
  },

  btn: {
    padding: "10px 22px",
    border: "none",
    cursor: "pointer",
    background: "#2c3e50",
    color: "#fff",
    borderRadius: "4px",
    fontSize: "15px",
  },
};

export default CertificateTemplate;
