import React, { useState } from 'react';
import { verifyCertificate } from '../services/certificateStorage';
import CertificateTemplate from '../components/CertificateTemplate';

const VerifyCertificate = () => {
  const [certificateId, setCertificateId] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleVerify = () => {
    if (!certificateId.trim()) {
      setResult({
        valid: false,
        error: 'Please enter a Certificate ID'
      });
      return;
    }

    setLoading(true);
    
    // Use setTimeout to simulate API delay
    setTimeout(() => {
      // THIS IS WHERE REAL VERIFICATION HAPPENS
      // It gets the ACTUAL certificate data from storage
      const verificationResult = verifyCertificate(certificateId);
      
      setResult(verificationResult);
      setLoading(false);
    }, 500);
  };

  return (
    <div style={styles.container}>
      <h1>üîç Verify Certificate</h1>
      <p>Enter the Certificate ID to verify authenticity</p>
      
      <div style={styles.inputSection}>
        <input
          type="text"
          placeholder="Enter Certificate ID (e.g., CERT-1702886400000-abc123)"
          value={certificateId}
          onChange={(e) => setCertificateId(e.target.value)}
          style={styles.input}
        />
        <button 
          onClick={handleVerify} 
          disabled={loading}
          style={styles.verifyButton}
        >
          {loading ? 'Verifying...' : 'Verify'}
        </button>
      </div>

      {result && (
        <div style={result.valid ? styles.successCard : styles.errorCard}>
          {result.valid ? (
            <>
              <h2>‚úÖ Certificate Verified Successfully!</h2>
              <p>This certificate is valid and authentic.</p>
              
              {/* SHOWS ACTUAL CERTIFICATE DATA */}
              <div style={styles.certificateDisplay}>
                <CertificateTemplate certificateData={result.data} />
              </div>
              
              <div style={styles.verificationDetails}>
                <h4>Verification Details:</h4>
                <p><strong>Certificate ID:</strong> {result.data.certificateId}</p>
                <p><strong>Verified At:</strong> {new Date().toLocaleString()}</p>
                <p><strong>Issued To:</strong> {result.data.name}</p>
                <p><strong>Event:</strong> {result.data.event}</p>
                <p><strong>Status:</strong> <span style={{ color: 'green' }}>VALID</span></p>
              </div>
            </>
          ) : (
            <>
              <h2>‚ùå Certificate Not Found</h2>
              <p>{result.error}</p>
              {result.suggestions && (
                <div style={styles.suggestions}>
                  <h4>Suggestions:</h4>
                  <ul>
                    {result.suggestions.map((suggestion, index) => (
                      <li key={index}>{suggestion}</li>
                    ))}
                  </ul>
                </div>
              )}
            </>
          )}
        </div>
      )}

      <div style={styles.example}>
        <h4>Example Certificate IDs:</h4>
        <p style={{ fontFamily: 'monospace' }}>CERT-1702886400000-1001</p>
        <p style={{ fontFamily: 'monospace' }}>CERT-1702886400000-1002</p>
        <p><em>Note: These are examples. Use actual generated IDs.</em></p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    maxWidth: '1000px',
    margin: '0 auto'
  },
  inputSection: {
    display: 'flex',
    gap: '10px',
    margin: '30px 0'
  },
  input: {
    flex: 1,
    padding: '15px',
    fontSize: '16px',
    border: '2px solid #3498db',
    borderRadius: '5px'
  },
  verifyButton: {
    padding: '15px 30px',
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px'
  },
  successCard: {
    backgroundColor: '#d4edda',
    color: '#155724',
    padding: '25px',
    borderRadius: '10px',
    border: '1px solid #c3e6cb',
    marginTop: '20px'
  },
  errorCard: {
    backgroundColor: '#f8d7da',
    color: '#721c24',
    padding: '25px',
    borderRadius: '10px',
    border: '1px solid #f5c6cb',
    marginTop: '20px'
  },
  certificateDisplay: {
    margin: '30px 0'
  },
  verificationDetails: {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '8px',
    marginTop: '20px'
  },
  suggestions: {
    marginTop: '15px'
  },
  example: {
    marginTop: '40px',
    padding: '20px',
    backgroundColor: '#f8f9fa',
    borderRadius: '8px'
  }
};

export default VerifyCertificate;
