import React, { useState } from 'react';
import { verifyCertificate } from '../services/api';
import CertificateTemplate from '../components/CertificateTemplate';

const VerifyCertificate = () => {
  const [certificateId, setCertificateId] = useState('');
  const [verificationResult, setVerificationResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleVerify = async () => {
    if (!certificateId.trim()) return;
    
    setLoading(true);
    try {
      const result = await verifyCertificate(certificateId);
      setVerificationResult(result);
    } catch (error) {
      setVerificationResult({
        valid: false,
        error: 'Certificate not found or invalid'
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2>Verify Certificate</h2>
      
      <div style={styles.verificationBox}>
        <input
          type="text"
          placeholder="Enter Certificate ID (e.g., CERT-1766049906839)"
          value={certificateId}
          onChange={(e) => setCertificateId(e.target.value)}
          style={styles.input}
        />
        <button 
          onClick={handleVerify}
          disabled={loading}
          style={styles.verifyButton}
        >
          {loading ? 'Verifying...' : 'Verify Certificate'}
        </button>
        
        {certificateId && (
          <div style={styles.qrHint}>
            <p>Or scan QR code from certificate</p>
            <small>Certificate IDs start with "CERT-"</small>
          </div>
        )}
      </div>

      {verificationResult && (
        <div style={styles.resultContainer}>
          {verificationResult.valid ? (
            <>
              <div style={styles.successMessage}>
                ✅ Certificate Verified Successfully!
              </div>
              <div style={styles.certificatePreview}>
                <h3>Certificate Preview</h3>
                <CertificateTemplate certificateData={verificationResult.data} />
              </div>
            </>
          ) : (
            <div style={styles.errorMessage}>
              ❌ Invalid Certificate
              <p>{verificationResult.error}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto'
  },
  verificationBox: {
    backgroundColor: '#f8f9fa',
    padding: '30px',
    borderRadius: '10px',
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
  },
  input: {
    width: '100%',
    padding: '12px',
    fontSize: '16px',
    border: '2px solid #3498db',
    borderRadius: '5px',
    marginBottom: '15px'
  },
  verifyButton: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
    marginBottom: '15px'
  },
  qrHint: {
    textAlign: 'center',
    color: '#666',
    fontSize: '0.9em'
  },
  resultContainer: {
    marginTop: '30px'
  },
  successMessage: {
    backgroundColor: '#d4edda',
    color: '#155724',
    padding: '15px',
    borderRadius: '5px',
    marginBottom: '20px',
    fontSize: '18px',
    textAlign: 'center'
  },
  errorMessage: {
    backgroundColor: '#f8d7da',
    color: '#721c24',
    padding: '15px',
    borderRadius: '5px',
    textAlign: 'center'
  },
  certificatePreview: {
    marginTop: '20px'
  }
};

export default VerifyCertificate;
