import React, { useState, useRef } from 'react';
import { saveAs } from 'file-saver';
import JSZip from 'jszip';

import CertificateTemplate from '../components/CertificateTemplate';

const UploadCSV = () => {
  const [csvData, setCsvData] = useState([]);
  const [previewData, setPreviewData] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [generated, setGenerated] = useState(false);
  const fileInputRef = useRef(null);

  const parseCSV = (text) => {
    const lines = text.split('\n').filter(line => line.trim() !== '');
    
    if (lines.length === 0) {
      throw new Error('CSV file is empty');
    }

    const headers = lines[0].split(',').map(header => 
      header.trim().replace(/^"|"$/g, '')
    );

    const data = [];
    for (let i = 1; i < lines.length; i++) {
      const line = lines[i];
      
      // Simple CSV parsing
      const values = line.split(',').map(val => 
        val.trim().replace(/^"|"$/g, '')
      );
      
      if (values.length >= 2) {
        const obj = {};
        headers.forEach((header, index) => {
          if (index < values.length) {
            obj[header.toLowerCase()] = values[index];
          }
        });
        data.push(obj);
      }
    }
    
    return data;
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.name.toLowerCase().endsWith('.csv')) {
      setError('Please upload a CSV file (.csv)');
      return;
    }

    setError('');
    setGenerated(false);
    const reader = new FileReader();

    reader.onload = (e) => {
      try {
        const text = e.target.result;
        const data = parseCSV(text);
        
        if (data.length === 0) {
          setError('No valid data found in CSV');
          return;
        }

        // Check for required columns
        const hasName = data.every(item => item.name);
        const hasEvent = data.every(item => item.event);
        
        if (!hasName || !hasEvent) {
          setError('CSV must contain "name" and "event" columns');
          return;
        }

        setCsvData(data);
        setPreviewData(data.slice(0, 3));
      } catch (err) {
        setError(`Error parsing CSV: ${err.message}`);
      }
    };

    reader.onerror = () => {
      setError('Error reading file');
    };

    reader.readAsText(file);
  };

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  const generateCertificates = async () => {
    if (csvData.length === 0) {
      setError('No data to process');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Create certificates with IDs
      const certificates = csvData.map((row, index) => ({
        name: row.name || '',
        event: row.event || '',
        type: row.type || 'Participation',
        organizer: row.organizer || "College",
        certificateId: `CERT-${Date.now()}-${index + 1000}`,
        date: new Date().toLocaleDateString('en-US', {
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        }),
        generatedOn: new Date().toISOString()
      }));

      // Create ZIP file
      const zip = new JSZip();
      
      // Create folder for certificates
      const certFolder = zip.folder("Certificates");
      
      // Add each certificate as HTML file
      certificates.forEach((cert) => {
        const htmlContent = `
<!DOCTYPE html>
<html>
<head>
  <title>Certificate - ${cert.name}</title>
  <meta charset="UTF-8">
  <style>
    body { font-family: Arial; margin: 40px; }
    .cert { border: 2px solid #000; padding: 30px; max-width: 800px; margin: auto; }
    .header { text-align: center; border-bottom: 2px solid #3498db; padding-bottom: 20px; }
    .name { font-size: 36px; color: #2c3e50; text-align: center; margin: 20px 0; }
    .details { background: #f9f9f9; padding: 15px; margin: 20px 0; }
  </style>
</head>
<body>
  <div class="cert">
    <div class="header">
      <h1>CERTIFICATE OF ACHIEVEMENT</h1>
    </div>
    <p>This certifies that</p>
    <div class="name">${cert.name}</div>
    <p>has achieved <strong>${cert.type}</strong> in</p>
    <h3>"${cert.event}"</h3>
    <div class="details">
      <p><strong>Certificate ID:</strong> ${cert.certificateId}</p>
      <p><strong>Date:</strong> ${cert.date}</p>
      <p><strong>Organizer:</strong> ${cert.organizer}</p>
    </div>
    <p style="text-align: center;">‚úÖ Digitally Generated | üîí Secure | üì± QR Verifiable</p>
  </div>
</body>
</html>`;
        
        const fileName = `${cert.name.replace(/\s+/g, '_')}_Certificate.html`;
        certFolder.file(fileName, htmlContent);
        
        // Also add JSON file
        const jsonContent = JSON.stringify(cert, null, 2);
        certFolder.file(`${cert.name.replace(/\s+/g, '_')}_Metadata.json`, jsonContent);
      });

      // Create summary CSV
      const summaryHeaders = ['Name', 'Event', 'Type', 'Certificate ID', 'Date'];
      const summaryRows = certificates.map(cert => [
        cert.name,
        cert.event,
        cert.type,
        cert.certificateId,
        cert.date
      ]);
      const summaryCSV = [summaryHeaders.join(','), ...summaryRows.map(row => row.join(','))].join('\n');
      zip.file("Certificates_Summary.csv", summaryCSV);

      // Generate ZIP
      const zipBlob = await zip.generateAsync({ type: 'blob' });
      
      // Save the ZIP file
      saveAs(zipBlob, `Certificates_${new Date().getTime()}.zip`);
      
      setGenerated(true);
      alert(`‚úÖ Success! Generated ${certificates.length} certificates. Check your downloads folder.`);
      
    } catch (error) {
      console.error('Generation error:', error);
      setError(`Failed to generate certificates: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const downloadSampleCSV = () => {
    const sampleCSV = `name,event,type,organizer
Rahul Kumar,Cloud Bootcamp,winner,Computer Science Department
Abhiram KH,AMS Workshop,Participation,Engineering Department
Anjali S,AI Seminar,Participation,AI Research Center
John Doe,Web Development Competition,First Prize,IT Club
Priya Sharma,Robotics Workshop,Second Prize,Robotics Club`;
    
    const blob = new Blob([sampleCSV], { type: 'text/csv' });
    saveAs(blob, 'sample_certificates.csv');
  };

  const downloadSingleCertificate = () => {
    if (previewData.length === 0) return;
    
    const cert = previewData[0];
    const htmlContent = `
<!DOCTYPE html>
<html>
<head>
  <title>Certificate - ${cert.name}</title>
  <style>
    body { font-family: Arial; margin: 0; padding: 20px; }
    @media print { body { padding: 0; } }
  </style>
</head>
<body>
  <div style="border: 3px solid #000; padding: 30px; max-width: 800px; margin: auto;">
    <h1 style="text-align: center; color: #2c3e50;">CERTIFICATE</h1>
    <hr>
    <p style="text-align: center;">This is to certify that</p>
    <h2 style="text-align: center; color: #e74c3c;">${cert.name}</h2>
    <p style="text-align: center;">has participated in</p>
    <h3 style="text-align: center;">"${cert.event}"</h3>
    <p style="text-align: center;">and received <strong>${cert.type || 'Participation'}</strong></p>
    <div style="margin-top: 30px; padding: 15px; background: #f8f9fa;">
      <p>Date: ${new Date().toLocaleDateString()}</p>
      <p>Generated on: ${new Date().toLocaleString()}</p>
    </div>
  </div>
</body>
</html>`;
    
    const blob = new Blob([htmlContent], { type: 'text/html' });
    saveAs(blob, `${cert.name}_Certificate.html`);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.mainTitle}>üìÑ Certificate Generator</h1>
      <p style={styles.subtitle}>Upload a CSV file to generate digital certificates with QR codes</p>

      {error && (
        <div style={styles.errorBox}>
          <span style={styles.errorIcon}>‚ö†Ô∏è</span>
          <span>{error}</span>
        </div>
      )}

      {/* Upload Section */}
      <div style={styles.uploadSection}>
        <div 
          style={styles.uploadArea}
          onClick={handleUploadClick}
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => {
            e.preventDefault();
            if (e.dataTransfer.files[0]) {
              handleFileUpload({ target: { files: e.dataTransfer.files } });
            }
          }}
        >
          <input
            type="file"
            ref={fileInputRef}
            accept=".csv"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
          
          <div style={styles.uploadIcon}>üìÅ</div>
          <h3 style={styles.uploadTitle}>Upload CSV File</h3>
          <p style={styles.uploadText}>Click to browse or drag & drop your CSV file</p>
          <p style={styles.uploadHint}>Format: name,event,type,organizer</p>
        </div>

        <div style={styles.sampleSection}>
          <p style={styles.sampleText}>Need a sample file?</p>
          <button 
            onClick={downloadSampleCSV}
            style={styles.sampleButton}
          >
            üì• Download Sample CSV
          </button>
        </div>
      </div>

      {/* Preview Section */}
      {previewData.length > 0 && (
        <div style={styles.previewSection}>
          <h2 style={styles.sectionTitle}>üìã Data Preview</h2>
          <p style={styles.sectionSubtitle}>Showing {previewData.length} of {csvData.length} entries</p>
          
          <div style={styles.tableContainer}>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.th}>#</th>
                  <th style={styles.th}>Name</th>
                  <th style={styles.th}>Event</th>
                  <th style={styles.th}>Type</th>
                  <th style={styles.th}>Status</th>
                </tr>
              </thead>
              <tbody>
                {previewData.map((row, index) => (
                  <tr key={index} style={index % 2 === 0 ? styles.trEven : styles.trOdd}>
                    <td style={styles.td}>{index + 1}</td>
                    <td style={{ ...styles.td, fontWeight: 'bold' }}>{row.name}</td>
                    <td style={styles.td}>{row.event}</td>
                    <td style={styles.td}>
                      <span style={{
                        ...styles.badge,
                        backgroundColor: row.type === 'winner' ? '#2ecc71' : 
                                       row.type === 'First Prize' ? '#f39c12' : '#3498db'
                      }}>
                        {row.type || 'Participation'}
                      </span>
                    </td>
                    <td style={styles.td}>
                      {row.certificateId ? (
                        <span style={{ ...styles.badge, backgroundColor: '#27ae60' }}>
                          ‚úÖ Generated
                        </span>
                      ) : (
                        <span style={{ ...styles.badge, backgroundColor: '#f39c12' }}>
                          ‚è≥ Pending
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Action Buttons */}
          <div style={styles.actionButtons}>
            <button
              onClick={generateCertificates}
              disabled={loading}
              style={loading ? styles.buttonLoading : styles.buttonGenerate}
            >
              {loading ? (
                <>
                  <span style={styles.spinner}></span>
                  Generating {csvData.length} Certificates...
                </>
              ) : (
                `üöÄ Generate All Certificates (${csvData.length})`
              )}
            </button>
            
            <button
              onClick={downloadSingleCertificate}
              style={styles.buttonSingle}
            >
              üìÑ Download Sample Certificate
            </button>
          </div>

          {generated && (
            <div style={styles.successBox}>
              <span style={styles.successIcon}>‚úÖ</span>
              <span>Certificates generated successfully! Check your downloads folder.</span>
            </div>
          )}
        </div>
      )}

      {/* Certificate Preview */}
      {previewData.length > 0 && (
        <div style={styles.previewCertificate}>
          <h2 style={styles.sectionTitle}>üéñÔ∏è Certificate Preview</h2>
          <div style={{ transform: 'scale(0.85)', transformOrigin: 'top center' }}>
            <CertificateTemplate 
              certificateData={{
                ...previewData[0],
                certificateId: 'CERT-PREVIEW-001',
                date: new Date().toLocaleDateString('en-US', {
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric'
                }),
                organizer: previewData[0].organizer || "Your College"
              }}
            />
          </div>
        </div>
      )}

      {/* Instructions */}
      <div style={styles.instructions}>
        <h3>üìù How to Use:</h3>
        <ol style={styles.instructionList}>
          <li>Download the sample CSV template</li>
          <li>Edit with your participant data</li>
          <li>Upload the CSV file</li>
          <li>Preview and generate certificates</li>
          <li>Download ZIP file with all certificates</li>
        </ol>
      </div>
    </div>
  );
};

