import React, { useState, useRef } from 'react';
import { saveMultipleCertificates } from '../services/certificateStorage';
import CertificateTemplate from '../components/CertificateTemplate';
import { generatePDF } from '../services/pdfGenerator';

const UploadCSV = () => {
  const [certificates, setCertificates] = useState([]);
  const [previewData, setPreviewData] = useState([]);
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef(null);

  const parseCSV = (text) => {
    const lines = text.split('\n').filter(line => line.trim() !== '');
    const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
    
    return lines.slice(1).map(line => {
      const values = line.split(',').map(v => v.trim());
      const obj = {};
      headers.forEach((header, index) => {
        if (index < values.length) {
          obj[header] = values[index];
        }
      });
      return obj;
    }).filter(row => row.name && row.event);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const data = parseCSV(e.target.result);
        const withIds = data.map((row, index) => ({
          ...row,
          certificateId: `CERT-${Date.now()}-${1000 + index}`,
          date: new Date().toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          }),
          organizer: row.organizer || 'College',
          type: row.type || 'Participation'
        }));

        setCertificates(withIds);
        setPreviewData(withIds.slice(0, 3));
      } catch (error) {
        alert('Error parsing CSV: ' + error.message);
      }
    };
    reader.readAsText(file);
  };

  const generateAllCertificates = async () => {
    if (certificates.length === 0) return;
    
    setLoading(true);
    try {
      // 1. SAVE to storage (THIS IS KEY FOR VERIFICATION)
      saveMultipleCertificates(certificates);
      
      // 2. Generate PDFs
      for (const cert of certificates) {
        await generatePDF(cert);
      }
      
      alert(`‚úÖ Successfully generated ${certificates.length} certificates!\nThey can now be verified using their Certificate IDs.`);
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1>üìÑ Upload CSV & Generate Certificates</h1>
      
      <div style={styles.uploadArea} onClick={() => fileInputRef.current.click()}>
        <input
          type="file"
          ref={fileInputRef}
          accept=".csv"
          onChange={handleFileUpload}
          style={{ display: 'none' }}
        />
        <div style={styles.uploadIcon}>üìÅ</div>
        <h3>Click to Upload CSV</h3>
        <p>Format: name,event,type,organizer</p>
      </div>

      {previewData.length > 0 && (
        <>
          <div style={styles.preview}>
            <h3>Preview ({certificates.length} certificates)</h3>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Event</th>
                  <th>Type</th>
                  <th>Certificate ID</th>
                </tr>
              </thead>
              <tbody>
                {previewData.map((cert, index) => (
                  <tr key={index}>
                    <td>{cert.name}</td>
                    <td>{cert.event}</td>
                    <td>{cert.type}</td>
                    <td style={{ fontFamily: 'monospace' }}>{cert.certificateId}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            
            <button 
              onClick={generateAllCertificates}
              disabled={loading}
              style={styles.generateButton}
            >
              {loading ? 'Generating...' : `Generate ${certificates.length} Certificates`}
            </button>
          </div>

          <div style={styles.designPreview}>
            <h3>Certificate Design Preview</h3>
            <CertificateTemplate certificateData={previewData[0]} />
          </div>
        </>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    maxWidth: '1000px',
    margin: '0 auto'
  },
  uploadArea: {
    border: '3px dashed #3498db',
    borderRadius: '15px',
    padding: '50px',
    textAlign: 'center',
    cursor: 'pointer',
    margin: '30px 0',
    backgroundColor: '#f8fafc'
  },
  uploadIcon: {
    fontSize: '50px',
    marginBottom: '15px'
  },
  preview: {
    backgroundColor: 'white',
    padding: '25px',
    borderRadius: '10px',
    boxShadow: '0 5px 20px rgba(0,0,0,0.1)',
    marginBottom: '30px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    margin: '20px 0'
  },
  tableTh: {
    backgroundColor: '#2c3e50',
    color: 'white',
    padding: '12px',
    textAlign: 'left'
  },
  tableTd: {
    border: '1px solid #eee',
    padding: '12px'
  },
  generateButton: {
    width: '100%',
    padding: '20px',
    backgroundColor: '#2ecc71',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    fontSize: '18px',
    cursor: 'pointer',
    marginTop: '20px'
  },
  designPreview: {
    textAlign: 'center',
    padding: '20px',
    backgroundColor: 'white',
    borderRadius: '10px',
    boxShadow: '0 5px 20px rgba(0,0,0,0.1)'
  }
};

export default UploadCSV;
