import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminLogin = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Simple admin credentials (in production, use proper authentication)
  const ADMIN_CREDENTIALS = {
    username: 'admin',
    password: 'admin123'
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Simulate API call
    setTimeout(() => {
      if (credentials.username === ADMIN_CREDENTIALS.username && 
          credentials.password === ADMIN_CREDENTIALS.password) {
        localStorage.setItem('adminLoggedIn', 'true');
        navigate('/certificates');
      } else {
        setError('Invalid username or password');
      }
      setLoading(false);
    }, 1000);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div style={styles.container}>
      <div style={styles.loginCard}>
        <div style={styles.loginHeader}>
          <span style={styles.loginIcon}>üîê</span>
          <h1 style={styles.loginTitle}>Admin Login</h1>
          <p style={styles.loginSubtitle}>
            Access the certificate management dashboard
          </p>
        </div>

        <form onSubmit={handleSubmit} style={styles.form}>
          {error && (
            <div style={styles.errorAlert}>
              <span style={styles.errorIcon}>‚ö†Ô∏è</span>
              <span style={styles.errorText}>{error}</span>
            </div>
          )}

          <div style={styles.formGroup}>
            <label style={styles.label}>Username</label>
            <input
              type="text"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              style={styles.input}
              placeholder="Enter admin username"
              required
              disabled={loading}
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Password</label>
            <input
              type="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              style={styles.input}
              placeholder="Enter password"
              required
              disabled={loading}
            />
          </div>

          <button 
            type="submit" 
            style={styles.submitButton}
            disabled={loading}
          >
            {loading ? (
              <>
                <span style={styles.spinner}></span>
                Signing In...
              </>
            ) : (
              'üîë Sign In'
            )}
          </button>

          <div style={styles.loginNote}>
            <p style={styles.noteText}>
              Default credentials: admin / admin123
            </p>
            <p style={styles.noteTextSmall}>
              Note: This is a demo. In production, use secure authentication.
            </p>
          </div>
        </form>

        <div style={styles.backSection}>
          <button 
            onClick={() => navigate('/')}
            style={styles.backButton}
          >
            ‚Üê Back to Home
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '80vh',
    fontFamily: "'Inter', sans-serif",
  },
  loginCard: {
    backgroundColor: 'white',
    padding: '50px',
    borderRadius: '16px',
    boxShadow: '0 8px 40px rgba(0, 0, 0, 0.12)',
    border: '1px solid #e5e7eb',
    width: '100%',
    maxWidth: '480px',
  },
  loginHeader: {
    textAlign: 'center',
    marginBottom: '40px',
  },
  loginIcon: {
    fontSize: '64px',
    marginBottom: '20px',
    display: 'block',
  },
  loginTitle: {
    fontSize: '32px',
    fontWeight: 'bold',
    color: '#1f2937',
    marginBottom: '10px',
  },
  loginSubtitle: {
    fontSize: '16px',
    color: '#6b7280',
    margin: '0',
  },
  form: {
    marginBottom: '30px',
  },
  errorAlert: {
    backgroundColor: '#fee2e2',
    color: '#dc2626',
    padding: '15px',
    borderRadius: '8px',
    marginBottom: '25px',
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
  },
  errorIcon: {
    fontSize: '20px',
  },
  errorText: {
    fontSize: '14px',
    fontWeight: '500',
  },
  formGroup: {
    marginBottom: '25px',
  },
  label: {
    display: 'block',
    fontSize: '14px',
    fontWeight: '600',
    color: '#374151',
    marginBottom: '8px',
  },
  input: {
    width: '100%',
    padding: '15px 20px',
    borderRadius: '8px',
    border: '2px solid #e5e7eb',
    fontSize: '16px',
    transition: 'border-color 0.2s',
    outline: 'none',
  },
  inputFocus: {
    borderColor: '#1a237e',
  },
  submitButton: {
    width: '100%',
    backgroundColor: '#1a237e',
    color: 'white',
    border: 'none',
    padding: '18px',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '10px',
    marginTop: '10px',
    boxShadow: '0 4px 15px rgba(26, 35, 126, 0.3)',
  },
  spinner: {
    width: '20px',
    height: '20px',
    border: '2px solid rgba(255,255,255,0.3)',
    borderTopColor: 'white',
    borderRadius: '50%',
    animation: 'spin 1s linear infinite',
  },
  loginNote: {
    marginTop: '25px',
    padding: '15px',
    backgroundColor: '#f8fafc',
    borderRadius: '8px',
    textAlign: 'center',
  },
  noteText: {
    fontSize: '14px',
    color: '#6b7280',
    marginBottom: '5px',
  },
  noteTextSmall: {
    fontSize: '12px',
    color: '#9ca3af',
    margin: '0',
  },
  backSection: {
    textAlign: 'center',
    borderTop: '1px solid #e5e7eb',
    paddingTop: '25px',
  },
  backButton: {
    backgroundColor: 'transparent',
    color: '#6b7280',
    border: 'none',
    fontSize: '14px',
    fontWeight: '500',
    cursor: 'pointer',
    display: 'inline-flex',
    alignItems: 'center',
    gap: '5px',
  },
};

export default AdminLogin;
