import React from 'react';
import { QRCodeSVG } from 'qrcode.react';

const CertificateTemplate = ({ certificateData }) => {
  const {
    name = "Rahul Kumar",
    event = "Cloud Bootcamp",
    type = "Winner",
    date = "18 December 2025",
    certificateId = "CERT-" + Date.now(),
    organizer = "College"
  } = certificateData || {};

  // Generate verification URL
  const verificationUrl = `${window.location.origin}/verify/${certificateId}`;
  
  // QR Code data
  const qrData = JSON.stringify({
    id: certificateId,
    name: name,
    event: event,
    award: type,
    date: date,
    verifyUrl: verificationUrl
  });

  return (
    <div style={styles.certificateContainer}>
      {/* Decorative Border */}
      <div style={styles.borderDecoration}></div>
      
      {/* Certificate Header */}
      <div style={styles.header}>
        <div style={styles.seal}>üèÜ</div>
        <h1 style={styles.title}>CERTIFICATE OF ACHIEVEMENT</h1>
        <p style={styles.subtitle}>Digitally Signed & Verifiable</p>
      </div>

      {/* Certificate Body */}
      <div style={styles.body}>
        <div style={styles.watermark}>CERTIFICATE</div>
        
        <p style={styles.text}>This is to certify that</p>
        
        <h2 style={styles.winnerName}>{name}</h2>
        
        <p style={styles.text}>has successfully achieved</p>
        
        <div style={styles.awardBadge}>
          <span style={styles.awardType}>{type.toUpperCase()}</span>
        </div>
        
        <p style={styles.text}>in the event</p>
        
        <h3 style={styles.eventName}>"{event}"</h3>
        
        <p style={styles.text}>organized by</p>
        <p style={styles.organizer}>{organizer}</p>

        {/* Details Section */}
        <div style={styles.detailsGrid}>
          <div style={styles.detailItem}>
            <strong>Date:</strong> {date}
          </div>
          <div style={styles.detailItem}>
            <strong>Certificate ID:</strong> {certificateId}
          </div>
          <div style={styles.detailItem}>
            <strong>Type:</strong> {type}
          </div>
          <div style={styles.detailItem}>
            <strong>Status:</strong> ‚úÖ Verified
          </div>
        </div>

        {/* QR Code Section */}
        <div style={styles.qrSection}>
          <div style={styles.qrContainer}>
            <QRCodeSVG 
              value={qrData}
              size={120}
              level="H"
              includeMargin={true}
              bgColor="#ffffff"
              fgColor="#2c3e50"
            />
            <p style={styles.qrLabel}>Scan to Verify</p>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div style={styles.footer}>
        <span style={styles.footerItem}>‚úÖ Digitally Generated</span>
        <span style={styles.footerItem}>üîí Secure</span>
        <span style={styles.footerItem}>üì± QR Verifiable</span>
      </div>
    </div>
  );
};

const styles = {
  certificateContainer: {
    width: '800px',
    minHeight: '600px',
    margin: '20px auto',
    backgroundColor: '#ffffff',
    borderRadius: '15px',
    boxShadow: '0 10px 40px rgba(0,0,0,0.15)',
    position: 'relative',
    border: '15px solid #f8f8f8',
    fontFamily: "'Georgia', serif",
    overflow: 'hidden'
  },
  borderDecoration: {
    position: 'absolute',
    top: '10px',
    left: '10px',
    right: '10px',
    bottom: '10px',
    border: '2px solid #f1c40f',
    borderRadius: '5px',
    pointerEvents: 'none'
  },
  header: {
    background: 'linear-gradient(90deg, #3498db, #2c3e50)',
    padding: '30px',
    textAlign: 'center',
    position: 'relative'
  },
  seal: {
    position: 'absolute',
    top: '15px',
    left: '20px',
    width: '60px',
    height: '60px',
    background: 'gold',
    borderRadius: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '24px',
    border: '3px solid #d4af37',
    boxShadow: '0 0 15px rgba(212, 175, 55, 0.5)'
  },
  title: {
    color: 'white',
    fontSize: '2.5em',
    margin: '0',
    textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
  },
  subtitle: {
    color: '#ecf0f1',
    fontSize: '1.1em',
    marginTop: '10px',
    opacity: '0.9'
  },
  body: {
    padding: '40px',
    backgroundImage: 'url("https://www.transparenttextures.com/patterns/cream-paper.png")',
    minHeight: '500px',
    position: 'relative'
  },
  watermark: {
    position: 'absolute',
    fontSize: '100px',
    opacity: '0.05',
    color: '#2c3e50',
    transform: 'rotate(-45deg)',
    top: '40%',
    left: '15%',
    fontWeight: 'bold',
    pointerEvents: 'none'
  },
  text: {
    textAlign: 'center',
    fontSize: '1.2em',
    color: '#555',
    margin: '10px 0'
  },
  winnerName: {
    textAlign: 'center',
    fontSize: '3em',
    color: '#2c3e50',
    margin: '20px 0',
    fontWeight: 'bold',
    fontFamily: "'Brush Script MT', cursive",
    background: 'linear-gradient(90deg, #e74c3c, #3498db)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent'
  },
  awardBadge: {
    textAlign: 'center',
    margin: '20px 0'
  },
  awardType: {
    background: 'linear-gradient(90deg, #ff7e5f, #feb47b)',
    color: 'white',
    padding: '10px 30px',
    borderRadius: '50px',
    fontSize: '1.5em',
    fontWeight: 'bold',
    boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
  },
  eventName: {
    textAlign: 'center',
    fontSize: '2em',
    color: '#e74c3c',
    margin: '15px 0',
    fontStyle: 'italic'
  },
  organizer: {
    textAlign: 'center',
    fontSize: '1.3em',
    color: '#3498db',
    fontWeight: 'bold',
    margin: '10px 0'
  },
  detailsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(2, 1fr)',
    gap: '20px',
    margin: '40px 0',
    padding: '20px',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    borderRadius: '10px',
    border: '2px dashed #3498db'
  },
  detailItem: {
    fontSize: '1.1em',
    color: '#2c3e50'
  },
  qrSection: {
    position: 'absolute',
    bottom: '20px',
    right: '30px',
    textAlign: 'center'
  },
  qrContainer: {
    backgroundColor: 'white',
    padding: '10px',
    borderRadius: '10px',
    border: '2px solid #3498db',
    boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
  },
  qrLabel: {
    fontSize: '0.9em',
    color: '#666',
    marginTop: '8px'
  },
  footer: {
    backgroundColor: '#2c3e50',
    color: 'white',
    padding: '15px',
    display: 'flex',
    justifyContent: 'space-around',
    alignItems: 'center'
  },
  footerItem: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px'
  }
};

export default CertificateTemplate;
