            <p style={styles.signatureName}>Mariana Napolitani</p>
            <p style={styles.signatureTitle}>Director of Education</p>
          </div>
        </div>

        {/* Footer Section */}
        <div style={styles.footerSection}>
          {/* Left: Issuer */}
          <div style={styles.issuerInfo}>
            <p style={styles.issuerLabel}>ISSUED BY</p>
            <p style={styles.issuerValue}>{data.organizer}</p>
          </div>

          {/* Center: Certificate ID */}
          <div style={styles.certIdInfo}>
            <p style={styles.certIdLabel}>CERTIFICATE ID</p>
            <p style={styles.certIdValue}>{data.certificateId}</p>
          </div>

          {/* Right: QR Code */}
          <div style={styles.qrSection}>
            <div style={styles.qrContainer}>
              <QRCodeSVG
                value={qrData}
                size={80}
                level="H"
                bgColor="#ffffff"
                fgColor="#000000"
              />
              <p style={styles.qrText}>Scan to verify</p>
            </div>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div style={styles.actionButtons}>
        <button 
          onClick={handleDownloadPDF} 
          style={styles.downloadBtn}
          disabled={downloading}
        >
          {downloading ? '‚è≥ Generating PDF...' : '‚¨áÔ∏è Download Certificate'}
        </button>
        <button onClick={handlePrint} style={styles.printBtn}>
          üñ®Ô∏è Print Certificate
        </button>
      </div>
    </div>
  );
};

